            msg.$wrapper.find('.modal-dialog').css("max-width", "90%");
            setTimeout(function(){
                window.addEventListener('message', myEventHandler);
                let modal =document.querySelector('.modal')

                observer.observe(modal, { attributes: true, attributeFilter: ['style'] });    
            },1000)



})

 // Create an observer instance
const observer = new MutationObserver((mutationsList, observer) => {
  // Use traditional 'for loops' for IE 11
  for(const mutation of mutationsList) {
    if (mutation.type === 'attributes' && mutation.attributeName === 'style') {
      const style = mutation.target.getAttribute('style');

      if (style.includes('display: none')) {
        window.removeEventListener('message', myEventHandler);
      }
    }
  }
});


function myEventHandler(event) {
  if(event.isTrusted && event.data){
      const data = event.data.split('|')
      if (data.length==2){
          if(data[0]=="view_reservation_detail"){
              window.location = "/frontdesk/reservation-detail/" + data[1]
          }else if (data[0] == "view_reservation_stay_detail"){
                            window.location = "/frontdesk/stay-detail/" + data[1]
          }

      }
  }
}

