    const getDate = r.message.working_date
    const dateConvert = convertDate(getDate)
    root_element.querySelector('#date').textContent=dateConvert || ""


    console.log(dateConvert)


    function convertDate(inputDate) {
      const date = new Date(inputDate);

      // Define formatting options
      const options = {
        weekday: 'long',
        day: 'numeric',
        month: 'long',
        year: 'numeric',
      };

      // Format the date using Intl.DateTimeFormat
      const formattedDate = new Intl.DateTimeFormat('en-US', options).format(date);

      // Split the formatted date into components
      const [weekday, day, month, year] = formattedDate.split(', ').map(item => item.trim());

      // Return an object with individual components
      return {
        weekday,
        day,
        month,
        year,
      };
    }
})

