frappe.call("edoor.api.frontdesk.get_daily_property_summary").then((r)=>{

    console.log(r.message.working_date)

    function parseDateString(dateString) {
    // Assuming dateString is in the format "YYYY-MM-DD"
    const [year, month, day] = dateString.split('-');

    // Convert month from number to month name
    const months = [
        'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',
        'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'
    ];
    const monthName = months[parseInt(month, 10) - 1];

    const dateObject = new Date(`${monthName} ${day}, ${year}`);

    // Get the day of the week
    const daysOfWeek = [
        'Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'
    ];
    const dayOfWeek = daysOfWeek[dateObject.getDay()];

    return {
        day: parseInt(day, 10),
        month: monthName,
        year: parseInt(year, 10),
        dayOfWeek: dayOfWeek
    };
}


const parsedDate = parseDateString(r.message.working_date);

root_element.querySelector('#name_day').textContent=parsedDate.dayOfWeek || ""
root_element.querySelector('#day').textContent=parsedDate.day || ""
root_element.querySelector('#year').textContent=parsedDate.month + ' ' + parsedDate.year  || ""

})

