}


function formatDate(inputDateString) {
  // Create a Date object from the input string
  var inputDate = new Date(inputDateString);

  // Extract components
  var day = inputDate.getDate();
  var month = inputDate.getMonth() + 1; // Months are zero-based, so add 1
  var year = inputDate.getFullYear();

  // Format components with leading zeros if necessary
  var formattedDay = (day < 10) ? '0' + day : day;
  var formattedMonth = (month < 10) ? '0' + month : month;

  // Form the final formatted date string
  var formattedDateString = formattedDay + '-' + formattedMonth + '-' + year;

  return formattedDateString;
}

