script:
let default_sidebar = document.querySelector('.layout-side-section');
default_sidebar.remove()

let element = document.querySelector('.sidebar_menu');

if(element){
    element.remove();
}


let my_menu = document.createElement("div")
my_menu.classList.add("sidebar_menu")


frappe.db.get_list('App Sidebar Menu', {
    fields: ['name','parent_app_sidebar_menu', 'title','url','icon'],
    limit:10000,
    order_by:"sort_order"
    
}).then(result => {
    let main_menu = document.createElement("ul")
    main_menu.classList.add("main_menu")
    result.filter(r=>r.parent_app_sidebar_menu=='root_menu').forEach(m=>{
        let li = document.createElement("li")
        li.textContent = m.title
        
        //get child menu
        if(result.filter(r=>r.parent_app_sidebar_menu==m.name).length>0){
            let sub_menu = document.createElement("ul")
            sub_menu.classList.add("sub_menu")
            result.filter(r=>r.parent_app_sidebar_menu==m.name).forEach(s=>{
                   let sub_li = document.createElement("li")
                    sub_li.textContent = s.title
                    sub_menu.appendChild(sub_li)
                    
            })
            li.appendChild(sub_menu)
            
        }
        
    
        main_menu.appendChild(li)
        
                
    })
    my_menu.appendChild(main_menu)    
     
    document.querySelector("body").prepend(my_menu)
    

    
})

root_element.querySelector('button').addEventListener('click', function(){
    const el = document.querySelector('[custom_block_name="Member Check In"]').querySelector(".widget-body").children[0].shadowRoot.querySelector("iframe")
    el.requestFullscreen()
    el.contentWindow.postMessage("Hello from the main page!", "*");

})

window.addEventListener("message", (event)=>{
    if(event.data=="full_screen"){
        const el = document.querySelector('[custom_block_name="Member Check In"]').querySelector(".widget-body").children[0].shadowRoot.querySelector("iframe")
        el.requestFullscreen()
    }else if(event.data=="view_all_recent_membership_check_in"){
        document.exitFullscreen();
        frappe.set_route('List',"Membership Check In","List")
    }
}, false);

let parser = new DOMParser(); // create a DOMParser object

frappe.call('epos_restaurant_2023.api.gym.get_recent_checked_ins',{"limit":8}).then(data=>{

    let table    = document.createElement("table");
    let tbody = document.createElement("tbody");
    let tr = document.createElement("tr");
    let th = document.createElement("th");
    let td = document.createElement("td");


    th.innerHTML = "Check In No";
    tr.appendChild(th)

    th = document.createElement("th");
    th.innerHTML = "Check In Date" 
    tr.appendChild(th)

    th = document.createElement("th");
    th.innerHTML = "Creation" 
    tr.appendChild(th)

    th = document.createElement("th");
    th.innerHTML = "Member" 
    tr.appendChild(th)

    th = document.createElement("th");
    th.innerHTML = "Membership Type" 
    tr.appendChild(th)

    th = document.createElement("th");
    th.innerHTML = "Membership" 
    tr.appendChild(th)

    th = document.createElement("th");
    th.innerHTML = "Check In Count" 
    tr.appendChild(th)


    tbody.appendChild(tr)

    data.message.forEach((r)=>{

        //check in number
        tr = document.createElement("tr");
        td = document.createElement("td");
        td.textContent  = r.membership_check_in;
        tr.appendChild(td)

        //check in date
        td = document.createElement("td");
        td.textContent =frappe.format( r.check_in_date,{fieldtype:"Date"});
        tr.appendChild(td)

        //creation 
        td = document.createElement("td");
        td.textContent =frappe.format( r.creation,{fieldtype:"Datetime"});
        tr.appendChild(td)

         //member_name
        td = document.createElement("td");
        td.textContent  = r.member_name;
        tr.appendChild(td)

         //membership_type
        td = document.createElement("td");
        td.textContent  = r.membership_type;
        tr.appendChild(td)

        //membership_name
        td = document.createElement("td");
        td.textContent  = r.membership_name;
        tr.appendChild(td)

         //membership check in
        td = document.createElement("td");
         td.appendChild(parser.parseFromString(frappe.format( r.check_in_number,{fieldtype:"Float"}), "text/html").querySelector("div"));
        tr.appendChild(td)

        tr.addEventListener('click', function() {

            frappe.set_route('Form', 'Membership Check In',r.membership_check_in)
        });

        tbody.appendChild(tr)

    })
    table.appendChild(tbody)

    let recentHTML = root_element.querySelector('#recent-membership-check-in');
    recentHTML.innerHTML = ""
    recentHTML.appendChild(table);

})


//btn view
let btb_view = root_element.querySelector('#view_all_recent_data');
btb_view.addEventListener('click', function() {
     frappe.set_route('List',"Membership Check In","List")
});

