    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<center>\n<h4>{{_(\"Working Day Sale Product Summary Report\")}}</h4>\n<h5>{{ doc.name }}</h5>\n</center>\n"}, {"fieldtype": "Section Break", "label": "Working Day Information"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\r\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n \r\n{% set from = main %}\r\n{% set to = second %}\r\n{% if exch[0].from_currency != main.name %}\r\n    {% set  from = second%}\r\n    {% set to = main %}\r\n{% endif %}\r\n\r\n \r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n    <tr>\r\n        <td>{{_(\"Business Branch\")}}</td>\r\n        <td class=\"text-right\">{{doc.business_branch}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Outlet\")}}</td>\r\n        <td class=\"text-right\">{{doc.outlet}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"POS Profile\")}}</td>\r\n        <td class=\"text-right\">{{doc.pos_profile}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Created Date\")}}</td>\r\n        <td class=\"text-right\">{{frappe.format(doc.creation,{'fieldtype':'Datetime'})}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Opened By\")}}</td>\r\n        <td class=\"text-right\">{{ frappe.get_doc(\"User\", doc.owner).full_name}}</td>\r\n    </tr>\r\n{%if doc.is_closed %}\r\n    <tr>\r\n        <td>{{_(\"Status\")}}</td>\r\n        <td class=\"text-right\">{{_(\"Closed\")}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Closed Date\")}}</td>\r\n        <td class=\"text-right\">{{frappe.format(doc.modified,{'fieldtype':'Datetime'})}}</td>\r\n    </tr>\r\n     <tr>\r\n        <td>{{_(\"Closed By\")}}</td>\r\n        <td class=\"text-right\">{{ frappe.get_doc(\"User\", doc.modified_by).full_name}}</td>\r\n    </tr>\r\n    \r\n    \r\n    \r\n{%endif%}\r\n\r\n <tr>\r\n        <td>{{_(\"Exchange Rate\")}}</td>\r\n        \r\n        <td class=\"text-right\">\r\n         {{frappe.utils.fmt_money(1,currency=exch[0].from_currency, precision= from.custom_currency_precision)}} \r\n        = \r\n        {{frappe.utils.fmt_money(exch[0].exchange_rate_input,currency=exch[0].to_currency, precision=to.custom_currency_precision)}}\r\n\r\n        </td>\r\n    </tr>\r\n</table>"}, {"fieldtype": "Section Break", "label": "Sale Products"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": ""}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \r\n    set sale_transactions = frappe.db.sql(\"\r\n         select \r\n            p.product_category, \r\n            p.product_group, \r\n            concat(p.product_name,if(coalesce(p.portion,'')='','',concat('-', p.portion)),if(p.is_free=1,'-Free','')) as product_name, \r\n            sum(p.quantity) as qty, \r\n            sum(p.total_discount) as discount,\r\n            sum(p.total_revenue) as total_revenue,\r\n            sum(p.tax_1_amount) as tax_1_amount,\r\n            sum(p.tax_2_amount) as tax_2_amount,\r\n            sum(p.tax_3_amount) as tax_3_amount,\r\n            p.price, \r\n            sum(p.sub_total) as amount \r\n        from `tabSale Product` p\r\n        inner join `tabSale` s on s.name = p.parent\r\n        where \r\n            s.working_day = '{}' and \r\n            s.docstatus = 1 and\r\n            s.is_foc = 0\r\n        group by \r\n            product_category,\r\n            product_group,\r\n            product_name,\r\n            p.price,\r\n            p.is_free,\r\n            p.portion\r\n    \".format(doc.name),as_dict=1)\r\n%}\r\n{% set exch = frappe.db.sql(\"select  exchange_rate,to_currency from `tabCurrency Exchange` where posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<table class=\"table table-bordered report-list\">\r\n    <thead>\r\n \r\n        <tr>\r\n        \r\n        <th><strong>{{_(\"Product Name\")}}</strong></th>\r\n        \r\n        <th class = 'text-right'><strong>{{_(\"Price\")}}</strong></th>\r\n     \r\n        <th class = 'text-center'><strong>{{_(\"Qty\")}}</strong></th>\r\n        \r\n        <th class = 'text-right'><strong>{{_(\"Amount\")}}</strong></th>\r\n\r\n    </tr>\r\n    </thead>\r\n    <tbody>\r\n        {% for product_group in sale_transactions | map(attribute='product_group') | unique  %}\r\n        {% for product_category in sale_transactions |selectattr(\"product_group\", \"equalto\", product_group) |map(attribute='product_category') | unique  %}\r\n             <tr>\r\n                <td colspan=\"3\"><strong>{{product_group}} => {{product_category}}</strong></td>\r\n            </tr>\r\n\r\n        {% for p in sale_transactions |selectattr(\"product_category\", \"equalto\", product_category) %}\r\n            <tr>\r\n                <td>{{p.product_name}}</td>\r\n                <td align = 'right'>\r\n                    {{frappe.utils.fmt_money(p.price,currency=main.name, precision=main.custom_currency_precision)}}\r\n                </td>\r\n                <td align = 'center'>{{p.qty}}</td>\r\n                <td align = 'right'>\r\n                    {{frappe.utils.fmt_money(p.amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n                </td>\r\n            </tr>\r\n\r\n        {%endfor%}\r\n        <!--Start render total category-->\r\n            <tr>\r\n                <td colspan=\"2\" class=\"text-right\"><strong>{{_(\"Total\")}} - {{product_category}}</strong></td>\r\n                <td align = 'center'><strong>{{sale_transactions | selectattr(\"product_category\", \"equalto\", product_category)|sum(attribute='qty')}}</strong></td>\r\n                <td align = 'right'\r\n                    <strong>\r\n                        {{frappe.utils.fmt_money((sale_transactions | selectattr(\"product_category\", \"equalto\", product_category)|sum(attribute='amount')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                    </strong>\r\n                </td>\r\n            </tr>\r\n\r\n        {%endfor%}\r\n                <!--Start render total product_group-->\r\n            <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Total\")}} - {{product_group}}</strong></td>\r\n                <td align = 'center'><strong>{{sale_transactions | selectattr(\"product_group\", \"equalto\", product_group)|sum(attribute='qty')}}</strong></td>\r\n                <td align = 'right'><strong>\r\n                    {{frappe.utils.fmt_money((sale_transactions | selectattr(\"product_group\", \"equalto\", product_group)|sum(attribute='amount')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                </strong></td>\r\n            </tr>\r\n        {%endfor%}\r\n        \r\n\r\n </tbody>\r\n  <!--Start render footer sub total, discount tax and total revenue-->\r\n <tfoot>\r\n             <tr><td colspan=\"4\"></td></tr>\r\n             \r\n            <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Total Quantity\")}}</strong></td>\r\n                <td></td>\r\n                <td align = 'right'><strong>{{sale_transactions |sum(attribute='qty')}}</strong></td>\r\n            </tr>\r\n            <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Sub Total\")}}</strong></td>\r\n                <td></td>\r\n                <td align = 'right'><strong>\r\n                    {{frappe.utils.fmt_money((sale_transactions |sum(attribute='amount')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                </strong></td>\r\n            </tr>\r\n            <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Discount\")}}</strong></td>\r\n                <td></td>\r\n                <td align = 'right'><strong>\r\n                {{frappe.utils.fmt_money((sale_transactions |sum(attribute='discount')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                </strong></td>\r\n            </tr>\r\n          {% if sale_transactions |sum(attribute='tax_1_amount') > 0 %}\r\n            <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Tax 1\")}}</strong></td>\r\n                <td></td>\r\n                <td align = 'right'><strong>\r\n                    {{frappe.utils.fmt_money((sale_transactions |sum(attribute='tax_1_amount')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                </strong></td>\r\n            </tr>\r\n            {% endif %}\r\n            {% if sale_transactions |sum(attribute='tax_2_amount') > 0 %}\r\n            <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Tax 2\")}}</strong></td>\r\n                <td></td>\r\n                <td align = 'right'><strong>\r\n                {{frappe.utils.fmt_money((sale_transactions |sum(attribute='tax_2_amount')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                </strong></td>\r\n            </tr>\r\n            {% endif%}\r\n            {% if sale_transactions |sum(attribute='tax_3_amount') > 0 %}\r\n            <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Tax 3\")}}</strong></td>\r\n                <td></td>\r\n                <td align = 'right'><strong>\r\n                    {{frappe.utils.fmt_money((sale_transactions |sum(attribute='tax_3_amount')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                </strong></td>\r\n            </tr>\r\n            {% endif %}\r\n           \r\n          <tr>\r\n                <td colspan=\"2\" align = 'right'><strong>{{_(\"Total Revenue\")}}</strong></td>\r\n                <td></td>\r\n                <td align = 'right'><strong>\r\n                    {{frappe.utils.fmt_money((sale_transactions |sum(attribute='total_revenue')),currency=main.name, precision=main.custom_currency_precision)}}\r\n                    <br>= {{frappe.utils.fmt_money((sale_transactions |sum(attribute='total_revenue')) * exch[0].exchange_rate , currency=second.name, precision=second.custom_currency_precision)}}\r\n                </strong></td>\r\n               \r\n            </tr>\r\n\r\n            \r\n    </tfoot>\r\n</table>"}, {"fieldtype": "Section Break", "label": "Sale Summary by Revenue Group"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset sale_transactions = frappe.db.sql(\"\nselect revenue_group,\n    sum(sp.quantity) as total_qty,\n    sum(total_revenue) as amount \nfrom `tabSale Product` sp\n    inner join tabSale s on s.name = sp.parent\nwhere \n    s.working_day = '{}' and \n    s.is_foc=0 and \n    s.docstatus = 1\ngroup by revenue_group\".format(doc.name),as_dict=1)\n%}\n\n{% set exch = frappe.db.sql(\"select  exchange_rate from `tabCurrency Exchange` where posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n<table class=\"table table-bordered report-list\">\n    <thead>\n        <tr>\n            <th>{{_(\"Revenue Group\")}}</th>\n            <th class=\"text-center\">{{_(\"QTY\")}}</th>\n            <th class=\"text-right\">{{_(\"Amount\")}}</th>\n        </tr>\n        \n    </thead>\n    <tbody>\n        {% for t in sale_transactions %}\n        <tr>\n            <td>{{t.revenue_group}}</td>\n            <td class=\"text-center\">{{t.total_qty}}</td>\n            <td class=\"text-right\">\n                {{frappe.utils.fmt_money(t.amount,currency=main.name, precision=main.custom_currency_precision)}}\n            </td>\n            \n        </tr>\n        {% endfor %}\n    </tbody>\n    <tfoot>\n        <tr>\n            <td><strong>{{_(\"Total\")}} </strong></td>\n            <td  class=\"text-center\"><strong>{{sale_transactions |sum(attribute='total_qty')}}</strong></td>\n            <td class=\"text-right\"><strong>\n                {{frappe.utils.fmt_money((sale_transactions |sum(attribute='amount')),currency=main.name, precision=main.custom_currency_precision)}}\n                <br> = {{frappe.utils.fmt_money((sale_transactions |sum(attribute='amount')) * exch[0].exchange_rate,currency=second.name, precision=second.custom_currency_precision)}}\n            </strong></td>\n        </tr>\n    </tfoot>\n</table>\n\n\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset sale_transactions = frappe.db.sql(\"\nselect product_name,\n    sum(sp.quantity) as total_qty,\n    sum(total_revenue) as amount,\n    sp.portion,\n    concat(sp.product_name,if(sp.is_free=1,'-Free','')) as product_name, \n    sp.is_free\nfrom `tabSale Product` sp\n    inner join tabSale s on s.name = sp.parent\nwhere \n    s.working_day = '{}' and \n    s.is_foc=1 and \n    s.docstatus = 1\ngroup by product_name\".format(doc.name),as_dict=1)\n%}\n\n{% set exch = frappe.db.sql(\"select  exchange_rate from `tabCurrency Exchange` where posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n{% if sale_transactions %}\n    \n    <h4 class=\"col-sm-12\" style=\"padding-left:0;margin-bottom:10px!important\">FOC</h4>\n    <table class=\"table table-bordered report-list\">\n        <thead>\n            <tr>\n                <th>{{_(\"Product Name\")}}</th>\n                <th class=\"text-center\">{{_(\"QTY\")}}</th>\n                <th class=\"text-right\">{{_(\"Amount\")}}</th>\n            </tr>\n            \n        </thead>\n        <tbody>\n            {% for t in sale_transactions %}\n            <tr>\n                <td>{{t.product_name}}  {{t.portion}}</td>\n                <td class=\"text-center\">{{t.total_qty}}</td>\n                <td class=\"text-right\">\n                    {{frappe.utils.fmt_money(t.amount,currency=main.name, precision=main.custom_currency_precision)}}\n                </td>\n                \n            </tr>\n            {% endfor %}\n        </tbody>\n        <tfoot>\n            <tr>\n                <td><strong>{{_(\"Total\")}} </strong></td>\n                <td  class=\"text-center\"><strong>{{sale_transactions |sum(attribute='total_qty')}}</strong></td>\n                <td class=\"text-right\"><strong>\n                    {{frappe.utils.fmt_money((sale_transactions |sum(attribute='amount')),currency=main.name, precision=main.custom_currency_precision)}}\n                <br> = {{frappe.utils.fmt_money((sale_transactions |sum(attribute='amount')) * exch[0].exchange_rate,currency=second.name, precision=second.custom_currency_precision)}}\n                 </strong></td>\n                \n            </tr>\n            \n        </tfoot>\n    </table>\n{% endif %}\n\n"}]",
    "print_format_builder": 1,
    "print_format_builder_beta": 0,
    "doctype": "Print Format"
}

