    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "{% set date_format = frappe.db.sql(\"select TIME_FORMAT(creation, '%H:%i %p') as ptime,TIME_FORMAT(modified, '%H:%i %p') as ctime from `tabSale` where name='{}'\".format(doc.name),as_dict=1) %}\n\n{% if doc.docstatus == 1 %}\n    <h3 class=\"text-center\">INVOICE</h3>\n{% else %}\n    <h3 class=\"text-center\">DRAFT ITEM</h3>\n{% endif%}\n<table style=\"width:100%\" class=\"table-bill-header\">\n    <tr>\n        <td>{{_(\"Date\")}}: {{frappe.format_value(doc.posting_date,\"Date\")}}</td>\n        <td class=\"text-right\" style=\"white-space:nowrap\">{{_(\"Invoice No\")}}: {{doc.name}}</td>\n    </tr>\n    <tr>\n        <td style=\"white-space:nowrap\">{{_(\"Customer\")}}: {{doc.customer_name}}</td>\n        <td class=\"text-right\">{{_(\"Table No\")}}: {{doc.tbl_number}}</td>\n    </tr>\n    {% if docstatus == 0 %}\n    <tr>\n        <td>{{_(\"Opened\")}}: {{date_format[0].ptime}}</td>\n        <td class=\"text-right\" style=\"white-space:nowrap\">{{_(\"Cashier\")}}: {{doc.created_by}}</td>\n    </tr>\n    {% else %}\n    <tr>\n        <td>{{_(\"Opened\")}}: {{date_format[0].ptime}}</td>\n        <td class=\"text-right\" style=\"white-space:nowrap\">{{_(\"Closed\")}}: {{date_format[0].ctime}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Cashier\")}}: {{doc.closed_by}}</td>\n    </tr>\n    {%endif%}\n    {% if doc.note %}\n    <tr>\n        <td colspan=\"2\">{{_(\"Note\")}}: {{doc.note}}</td>\n        \n    </tr>\n    {% endif %}\n    \n    \n    \n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \r\n    set sale_transactions = frappe.db.sql(\"\r\n    select \r\n    p.product_code,\r\n    sum(p.quantity) as qty, \r\n    p.price,\r\n    p.discount,\r\n    p.discount_amount,\r\n    p.note,\r\n    concat(p.product_name,if(coalesce(p.portion,'')='','',concat('( ', p.portion, ' )')),\r\n    if(coalesce(p.modifiers,'')='','',concat(' - ', p.modifiers)),\r\n    if(p.is_free=1,' -Free',''))\r\n    as product_name,\r\n    if(p.discount_type = 'Amount','','%') as discount_type,\r\n    sum(p.sub_total) as amount,\r\n    s.customer_name \r\n    from `tabSale Product` p \r\n    inner join\r\n        `tabSale` s on s.name = p.parent \r\n    where \r\n        s.name = '{}' \r\n    group by \r\n    p.product_name,\r\n    p.price,\r\n    p.portion,\r\n    p.modifiers,\r\n    p.discount,\r\n    p.discount_amount,\r\n    p.note,\r\n    p.discount_type\r\n    \".format(doc.name),as_dict=1)%}\r\n    \r\n{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<table class=\"table report-list\">\r\n    <thead>\r\n        <tr>\r\n            <th class=\"text-left\">{{_(\"DESCRIPTION\")}}</th>\r\n            <th class=\"text-left\">{{_(\"QTY\")}}</th>\r\n            <th class=\"text-right\">{{_(\"PRICE\")}}</th>\r\n            <th class=\"text-right\">{{_(\"AMOUNT\")}}</th>\r\n        </tr>\r\n    </thead>\r\n    <tbody>\r\n        {% for p in sale_transactions %}\r\n        <tr>\r\n            <td class=\"text-left\">{{p.product_code}} - {{p.product_name}}</td>\r\n            <td class=\"text-left\">{{p.qty}}</td>\r\n            <td class=\"text-right\" style=\"white-space: nowrap;\">{{ frappe.utils.fmt_money(p.price,currency=main_currency.name, precision=main_currency.custom_currency_precision) }}</td>\r\n            <td class=\"text-right\" style=\"white-space: nowrap;\">{{frappe.utils.fmt_money(p.amount - p.discount_amount,currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n         </tr>\r\n            {% if p.discount > 0 %}\r\n                <tr>\r\n                    <td colspin='4'>{{_(\"Disc. \")}}( {{p.discount}} {{p.discount_type}}) : {{p.discount_amount}}</td>\r\n                </tr>\r\n            {%endif%}\r\n            {% if p.note != '' %}\r\n                <tr>\r\n                    <td colspin='4'>{{_(\"Note: \")}}{{p.note}}</td>\r\n                </tr>\r\n            {%endif%}\r\n       \r\n       \r\n        {% endfor %}\r\n    </tbody>\r\n</table>\r\n<div style=\"border-bottom: 1px solid #000000; margin-top: 12px\"></div>\r\n         \r\n     "}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<div class=\"tbl-list-summary\" style=\"margin-top:5px\">\r\n    <table class=\"ml-auto\">\r\n        {% if doc.sub_total != doc.grand_total %}\r\n        <tr>\r\n                <td style=\"width:150px\">{{_(\"Sub Total\")}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.sub_total, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        {% if doc.product_discount>0 %}\r\n        <tr>\r\n                <td>{{_(\"Item Discount\")}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.product_discount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        {% if doc.sale_discount>0 %}\r\n        <tr>\r\n                <td>\r\n                    {{_(\"Sale Discount\")}} \r\n                    {% if doc.discount_type == \"Percent\"%}\r\n                        ({{doc.discount}} %) \r\n                    {% endif %}\r\n                </td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.sale_discount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        {% if doc.product_discount>0 and doc.sale_discount >0 %}\r\n        <tr>\r\n                <td>{{_(\"Total Discount\")}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.total_discount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        \r\n        \r\n        {% if doc.tax_1_amount > 0 %}\r\n        <tr>\r\n                <td>{{_(\"Tax 1\")}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.tax_1_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        \r\n        {% if doc.tax_2_amount > 0 %}\r\n        <tr>\r\n                <td>{{_(\"Tax 2\")}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.tax_2_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        \r\n        {% if doc.tax_3_amount > 0 %}\r\n        <tr>\r\n                <td>{{_(\"Tax 3\")}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.tax_3_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        \r\n        {% if (1 if doc.tax_1_amount> 0 else 0) + (1 if doc.tax_2_amount> 0 else 0)  + (1 if doc.tax_3_amount> 0 else 0)  >=2  %}\r\n        <tr>\r\n                <td>{{_(\"Total Tax\")}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.total_tax, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        \r\n        \r\n        \r\n    </table>\r\n</div>"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<div class=\"grand_total\" >\r\n    <table class=\"ml-auto\" style=\"margin-top:0px\">\r\n       \r\n        <tr>\r\n               <td style=\"width:138px\">{{_(\"Grand Total\")}}({{main_currency.name}}) </td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.grand_total,currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n           <tr>\r\n                <td>{{_(\"Grand Total\")}}({{second_currency.name}}) </td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.grand_total * doc.exchange_rate,currency=second_currency.name, precision=second_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        \r\n        \r\n    </table>\r\n</div>"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<div class=\"tbl-list-summary\" style=\"margin-top:5px\">\r\n    <table class=\"ml-auto\">\r\n        \r\n        {% for p in doc.payment %}\r\n         <tr>\r\n                <td style=\"width:150px\">{{p.payment_type}}</td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(p.input_amount, currency=main_currency.currency,precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endfor %}\r\n        \r\n        {% if (doc.payment | length) > 1 %}\r\n         <tr>\r\n                <td>{{_(\"Total Paid\")}}</td>\r\n                \r\n                <td>{{frappe.format_value(doc.total_paid, \"Currency\")}}</td>\r\n        </tr>\r\n        \r\n        {% endif %}\r\n        \r\n        {% if doc.balance > 0 and doc.docstatus==1 %}\r\n             <tr>\r\n                <td>{{_(\"Balance\")}}({{main_currency.name}}) </td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.balance,currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n           <tr>\r\n                <td>{{_(\"Balance\")}}({{second_currency.name}}) </td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.balance * doc.exchange_rate,currency=second_currency.name, precision=second_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        \r\n        {% if doc.changed_amount > 0 %}\r\n             <tr>\r\n                <td>{{_(\"Changed\")}}({{main_currency.name}}) </td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.changed_amount,currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n           <tr>\r\n                <td>{{_(\"Changed\")}}({{second_currency.name}}) </td>\r\n                \r\n                <td>{{frappe.utils.fmt_money(doc.changed_amount * doc.exchange_rate,currency=second_currency.name, precision=second_currency.custom_currency_precision)}}</td>\r\n        </tr>\r\n        {% endif %}\r\n        \r\n        \r\n    </table>\r\n</div>"}]",
