n    {% if doc.hotel_star == 0.6 %}\n    <tr>\n        <td>{{_(\"Hotel Category\")}}</td>\n        <td>:</td>\n        <td>{{_(\"3 Star\")}}</td>\n    </tr>\n    {% endif %}\n    {% if doc.hotel_star == 0.7 %}\n    <tr>\n        <td>{{_(\"Hotel Category\")}}</td>\n        <td>:</td>\n        <td>{{_(\"3.5 Star\")}}</td>\n    </tr>\n    {% endif %}\n    {% if doc.hotel_star == 0.8 %}\n    <tr>\n        <td>{{_(\"Hotel Category\")}}</td>\n        <td>:</td>\n        <td>{{_(\"4 Star\")}}</td>\n    </tr>\n    {% endif %}\n    {% if doc.hotel_star == 0.9 %}\n    <tr>\n        <td>{{_(\"Hotel Category\")}}</td>\n        <td>:</td>\n        <td>{{_(\"4.5 Star\")}}</td>\n    </tr>\n    {% endif %}\n    {% if doc.hotel_star == 1 %}\n    <tr>\n        <td>{{_(\"Hotel Category\")}}</td>\n        <td>:</td>\n        <td>{{_(\"5 Star\")}}</td>\n    </tr>\n    {% endif %}\n</table>\n"}, {"fieldtype": "Section Break", "label": "Company Profile"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style=\"padding-top:10px\"></div>\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Company\")}}</td>\n        <td>:</td>\n        <td>{{doc.company_profile or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Contact Name\")}}</td>\n        <td>:</td>\n        <td>{{doc.company_contact_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Address\")}}</td>\n        <td>:</td>\n        <td>{{doc.company_address or ''}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style=\"padding-top:10px\"></div>\n<table class=\"table-no-boder\" style=\"margin-left:50px\">\n    <tr>\n        <td  width=\"110px\">{{_(\"Contact\")}}</td>\n        <td>:</td>\n        <td>{{doc.company_contact_phone_number or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Email\")}}</td>\n        <td>:</td>\n        <td>{{doc.company_email or ''}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Section Break", "label": "Guest Information"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style=\"padding-top:10px\"></div>\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Guest Group\")}}</td>\n        <td>:</td>\n        <td>{{doc.guest_group or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Guest Profile\")}}</td>\n        <td>:</td>\n        <td>{{doc.guest_profile or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Phone Number\")}}</td>\n        <td>:</td>\n        <td>{{doc.phone_number or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Email\")}}</td>\n        <td>:</td>\n        <td>{{doc.email or ''}}</td>\n    </tr>\n\n</table>"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style=\"padding-top:10px\"></div>\n<table class=\"table-no-boder\" style=\"margin-left:50px\">\n     <tr>\n        <td width=\"110px\">{{_(\"Nationality\")}}</td>\n        <td>:</td>\n        <td>{{doc.nationality or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Total Guest\")}}</td>\n        <td>:</td>\n        <td>{{doc.pax or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Adult\")}}</td>\n        <td>:</td>\n        <td>{{doc.adult or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Child\")}}</td>\n        <td>:</td>\n        <td>{{doc.child or ''}}</td>\n    </tr>\n</table>\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style=\"padding-top:5px\"></div>\n<div style=\"margin-left:5px\"><h5>{{_(\"Hotel Booking Detail: \")}}</h5></div>"}, {"fieldtype": "Section Break", "label": "Booking Room"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Reference No.\")}}</td>\n        <td>:</td>\n        <td>{{doc.booking_reference or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Arrival Date\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.arrival_date,{'fieldtype':'Date'}) or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Arrival Flight No.\")}}</td>\n        <td>:</td>\n        <td></td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Flight\")}}</td>\n        <td>:</td>\n        <td></td>\n    </tr>\n    {% for r in doc.room_types %}\n    <tr>\n        <td>{{_(\"Room Type\")}} ({{loop.index}})</td>\n        <td>:</td>\n        <td>{{r.room_type or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Number of Rooms\")}}</td>\n        <td>:</td>\n        <td>{{r.number_of_room or ''}}</td>\n    </tr>\n    {% endfor %}\n</table>"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n<table class=\"table-no-boder\" style=\"margin-left:50px\">\n    <tr>\n        <td width=\"110px\">{{_(\"Hotel Name\")}}</td>\n        <td>:</td>\n        <td>{{doc.hotel_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Date\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.departure_date,{'fieldtype':'Date'}) or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Arrival Time\")}}</td>\n        <td>:</td>\n        <td></td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Time\")}}</td>\n        <td>:</td>\n        <td></td>\n    </tr>\n     <tr>\n        <td>{{_(\"Number of Nights\")}}</td>\n        <td>:</td>\n        <td>{{doc.total_nights or ''}}</td>\n    </tr>\n    {% for r in doc.room_types %}\n    <tr>\n        <td>{{_(\"Room Rate\")}}</td>\n        <td>:</td>\n        <td>{{frappe.utils.fmt_money(r.rate,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% endfor %}\n</table>\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Inclusions\")}}</td>\n        <td>:</td>\n        <td>{{doc.inclusions or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Special Note\")}}</td>\n        <td>:</td>\n        <td>{{doc.note or ''}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set branch = frappe.get_doc(\"Business Branch\",doc.branch) %}\n<div>\n    <h4>Payment Detail:</h4>\n    \n</div>\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Bank Name\")}}</td>\n        <td>:</td>\n        <td>{{branch.bank_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Account Name\")}}</td>\n        <td>:</td>\n        <td>{{branch.account_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Account Number\")}}</td>\n        <td>:</td>\n        <td>{{branch.bank_account_number or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Swift Code\")}}</td>\n        <td>:</td>\n        <td>{{branch.swift_code or ''}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<table class=\"t-list\" style=\"margin-left:62px\">\r\n{% if doc.is_include_tax %}\r\n    {% set vat = (doc.vat / 100) + 1 %}\r\n    {% set total_before_tax = (doc.total_amount/vat)+doc.discount_amount %}\r\n    {% set grand_total = total_before_tax - doc.discount_amount + (total_before_tax - doc.discount_amount)*(vat - 1) %}\r\n    \r\n    <tr>\r\n        <td>{{_(\"Grand Total\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(grand_total,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Discount\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.discount_amount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% for type_of_payment in doc.payments | map(attribute='type_of_payment') | unique %}\r\n    <tr>\r\n        <td>{{type_of_payment}}</td>\r\n        <td> : </td>\r\n        <td align=\"right\" style = \"width:140px\">{{frappe.utils.fmt_money(doc.payments|selectattr(\"type_of_payment\", \"equalto\", type_of_payment)|sum(attribute='payment_amount'),currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% endfor %}\r\n    <tr>\r\n        <td>{{_(\"Balance\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(grand_total-doc.total_payment,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% else %}\r\n    {% set vat = (doc.vat / 100) + 1 %}\r\n    {% set total_before_tax = (doc.total_amount/vat)+doc.discount_amount %}\r\n    {% set grand_total = total_before_tax - doc.discount_amount %}\r\n    \r\n    <tr>\r\n        <td>{{_(\"Grand Total\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(grand_total,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Discount\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.discount_amount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% for type_of_payment in doc.payments | map(attribute='type_of_payment') | unique %}\r\n    <tr>\r\n        <td>{{type_of_payment}}</td>\r\n        <td> : </td>\r\n        <td align=\"right\" style = \"width:140px\">{{frappe.utils.fmt_money(doc.payments|selectattr(\"type_of_payment\", \"equalto\", type_of_payment)|sum(attribute='payment_amount'),currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% endfor %}\r\n    <tr>\r\n        <td>{{_(\"Balance\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(grand_total-doc.total_payment,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% endif %}\r\n</table>"}]",
