    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<center>\n    <h4 onclick=\"window.parent.postMessage('123456', '*')\">{{_(\"Close Working Day Summary\")}}</h4>\n    <h5>{{doc.name}}</h5>\n</center>"}, {"fieldtype": "Section Break", "label": "Working Day Information"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\r\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n \r\n{% set from = main %}\r\n{% set to = second %}\r\n{% if exch[0].from_currency != main.name %}\r\n    {% set  from = second%}\r\n    {% set to = main %}\r\n{% endif %}\r\n\r\n \r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n    <tr>\r\n        <td class=\"col-xs-5\">{{_(\"Working Day\")}}</td>\r\n        <td class=\"col-xs-8 text-right\">{{doc.name}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Business Branch\")}}</td>\r\n        <td class=\"text-right\">{{doc.business_branch}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Outlet\")}}</td>\r\n        <td class=\"text-right\">{{doc.outlet}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"POS Profile\")}}</td>\r\n        <td class=\"text-right\">{{doc.pos_profile}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Created Date\")}}</td>\r\n        <td class=\"text-right\">{{frappe.format(doc.creation,{'fieldtype':'Datetime'})}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Opened By\")}}</td>\r\n        <td class=\"text-right\">{{ frappe.get_doc(\"User\", doc.owner).full_name}}</td>\r\n    </tr>\r\n{%if doc.is_closed %}\r\n    <tr>\r\n        <td>{{_(\"Status\")}}</td>\r\n        <td class=\"text-right\">{{_(\"Closed\")}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Closed Date\")}}</td>\r\n        <td class=\"text-right\">{{frappe.format(doc.modified,{'fieldtype':'Datetime'})}}</td>\r\n    </tr>\r\n     <tr>\r\n        <td>{{_(\"Closed By\")}}</td>\r\n        <td class=\"text-right\">{{ frappe.get_doc(\"User\", doc.modified_by).full_name}}</td>\r\n    </tr>\r\n    \r\n    \r\n    \r\n{%endif%}\r\n\r\n <tr>\r\n        <td>{{_(\"Exchange Rate\")}}</td>\r\n        \r\n        <td class=\"text-right\">\r\n         {{frappe.utils.fmt_money(1,currency=exch[0].from_currency, precision= from.custom_currency_precision)}} \r\n        = \r\n        {{frappe.utils.fmt_money(exch[0].exchange_rate_input,currency=exch[0].to_currency, precision=to.custom_currency_precision)}}\r\n\r\n        </td>\r\n    </tr>\r\n</table>"}, {"fieldtype": "Section Break", "label": "Transaction"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \r\nset sale_transactions = frappe.db.sql(\"\r\nselect sale_type, count(name) as total_transaction, count(name) as total_display from `tabSale` \r\nwhere working_day = '{0}' and docstatus = 1 and is_foc=0  group by sale_type\r\nunion \r\nselect 'FOC', count(name) as total_transaction, count(name) as total_display from `tabSale` where working_day = '{0}' and docstatus = 1 and is_foc=1 \r\nunion\r\nselect 'Total Guest Cover' as sale_type, 0 as total_display, sum(guest_cover) as total_transaction from `tabSale` where working_day = '{0}'\r\n\".format(doc.name),as_dict=1)\r\n%}\r\n\r\n{% \r\nset close_cashier = frappe.db.sql(\"\r\nselect count(name) as total_shift  from `tabCashier Shift`\r\nwhere working_day='{}'\".format(doc.name),as_dict=1)\r\n%}\r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n    <tr>\r\n        <td class=\"col-xs-8\">{{_(\"Total Cashier Shift\")}}</td>\r\n        <td class=\"col-xs-8 text-right\">{{ close_cashier[0].total_shift }}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"# Transactions\")}}</td>\r\n        <td class=\"text-right\">{{sale_transactions |sum(attribute='total_transaction')}}</td>\r\n    </tr>\r\n    {% for t in sale_transactions %}\r\n        {% if t.total_transaction or t.total_display %}\r\n            <tr>\r\n                <td>{{t.sale_type}}</td>\r\n                <td class=\"text-right\">{{t.total_display}}</td>\r\n            </tr>\r\n        {% endif %}\r\n    {% endfor %}\r\n</table>\r\n"}, {"fieldtype": "Section Break", "label": "Sale Summary"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\r\n{% \r\n    set sale_transactions = frappe.db.sql(\"select \r\n        closed_by,\r\n     coalesce(sum(if(is_foc=0,sub_total,0)),0) as sub_total,\r\n    coalesce(sum(if(is_foc=0,total_discount,0)),0) as total_discount,\r\n    coalesce(sum(if(is_foc=0,tax_1_amount,0)),0) as tax_1,\r\n    coalesce(sum(if(is_foc=0,tax_2_amount,0)),0) as tax_2,\r\n    coalesce(sum(if(is_foc=0,tax_3_amount,0)),0) as tax_3,\r\n    coalesce(sum(if(is_foc = 1,grand_total,0)),0) as total_foc_amount,\r\n    coalesce(sum(if(is_foc = 0,grand_total,0)),0) as grand_total,\r\n    coalesce(sum(commission_amount),0) as total_commission_amount\r\n    from `tabSale`\r\n    where working_day = '{}' and\r\n        docstatus = 1\r\n    group by\r\n        closed_by\r\n        \r\n    \".format(doc.name),as_dict=1)\r\n%}\r\n\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n\r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n{% for t in sale_transactions %}\r\n        <tr>\r\n            <td colspan=\"2\"><strong> {{t.closed_by}}</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td class=\"col-xs-8\"  > <div style=\"padding-left:10px\">{{_(\"Sale Sub Total\")}} </div></td>\r\n            <td class=\"col-xs-8 text-right\">\r\n                 {{frappe.utils.fmt_money(t.sub_total,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% if t.total_discount %}\r\n            <tr>\r\n                <td> <div style=\"padding-left:10px\">{{_(\"Total Discount\")}} </div></td>\r\n                <td class=\"text-right\">\r\n                    {{frappe.utils.fmt_money(t.total_discount,currency=main.name, precision=main.custom_currency_precision)}}\r\n                </td>\r\n            </tr>\r\n        {% endif %}\r\n        <tr>\r\n            <td><div style=\"padding-left:10px\">{{_(\"Total Net Sale\")}}</div></td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.grand_total-t.total_commission_amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% if t.tax_1 %}\r\n        <tr>\r\n            <td><div style=\"padding-left:10px\">{{_(\"Tax 1\")}}</div></td>\r\n            <td class=\"text-right\">\r\n            {{frappe.utils.fmt_money(t.tax_1,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% endif %}\r\n        {% if t.tax_2 %}\r\n        <tr>\r\n            <td><div style=\"padding-left:10px\">{{_(\"Tax 2\")}}</div></td>\r\n            <td class=\"text-right\"> \r\n                {{frappe.utils.fmt_money(t.tax_2,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% endif %}\r\n        {% if t.tax_3 %}\r\n        <tr>\r\n            <td><div style=\"padding-left:10px\">{{_(\"Tax 3\")}}</div></td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.tax_3,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% endif %}\r\n        <tr>\r\n            <td><div style=\"padding-left:10px\">{{_(\"Total Sale Revenue\")}}</div></td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.grand_total-t.total_commission_amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% if t.total_foc_amount %}\r\n        <tr>\r\n            <td><div style=\"padding-left:10px\">{{_(\"Total FOC Amount\")}}</div></td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.total_foc_amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% endif %}\r\n        {% if t.total_commission_amount %}\r\n        <tr>\r\n            <td><div style=\"padding-left:10px\">{{_(\"Total Commission Amount\")}}</div></td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.total_commission_amount,currency=main.name, precision=main.custom_currency_precision)}}    \r\n            </td>\r\n        </tr>\r\n        {% endif %}\r\n\r\n{% endfor %}\r\n</table>"}, {"fieldtype": "Section Break", "label": "Sale By Revenue Group"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \r\nset sale_transactions = frappe.db.sql(\"\r\nselect revenue_group,\r\n    sum(sp.quantity) as total_qty,\r\n    sum(total_revenue) as amount \r\nfrom `tabSale Product` sp\r\n    inner join tabSale s on s.name = sp.parent\r\nwhere \r\n    s.working_day = '{}' and \r\n    s.is_foc=0 and \r\n    s.docstatus = 1\r\ngroup by revenue_group\".format(doc.name),as_dict=1)\r\n%}\r\n\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n\r\n\r\n<table class=\"table\" style = \"font-size:13px\">\r\n    <thead>\r\n        <tr>\r\n            <th>{{_(\"Revenue Group\")}}</th>\r\n            <th class=\"text-Left\">{{_(\"QTY\")}}</th>\r\n            <th class=\"text-right\">{{_(\"Amount\")}}</th>\r\n        </tr>\r\n    </thead>\r\n    <tbody>\r\n        {% for t in sale_transactions %}\r\n        <tr>\r\n            <td class=\"text-Left\">{{t.revenue_group}}</td>\r\n            <td class=\"text-Left\">{{t.total_qty}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n        {% endfor %}\r\n    </tbody>\r\n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \n    set sale_transactions = frappe.db.sql(\"\n    select revenue_group,\n    sum(total_revenue) as amount from `tabSale Product` sp\n    inner join tabSale s on s.name = sp.parent\n    where s.working_day = '{}' and s.is_foc=1 and s.docstatus=1\n    group by revenue_group\".format(doc.name),as_dict=1)\n%}\n\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n\n{% if sale_transactions |sum(attribute='amount') %}\n<h4>FOC By Revenue Group</h4>\n{% endif %}\n{% for t in sale_transactions %}\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    <tr>\n        <td class=\"col-xs-8\">{{t.revenue_group}}</td>\n        <td class=\"col-xs-8 text-right\">\n            {{frappe.utils.fmt_money(t.amount,currency=main.name, precision=main.custom_currency_precision)}}\n        </td>\n    </tr>\n</table>\n{% endfor %}\n\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset sale_transactions = frappe.db.sql(\"\nselect \n    payment_type,\n    symbol,\n    exchange_rate,\n    sum(fee_amount) as fee_amount ,\n    sum(input_amount) as input_amount ,\n    sum(payment_amount) as payment_amount\nfrom `tabSale Payment`\nwhere working_day = '{}' and docstatus = 1\ngroup by \n    payment_type,\n    symbol,\n    exchange_rate\n   \".format(doc.name),as_dict=1)\n%}\n{% set sale_tip = frappe.db.sql(\"\n                    select \n                        sum(tip_amount) as tip_amount\n                    from `tabSale`\n                    where working_day = '{}' and docstatus = 1\n\".format(doc.name),as_dict=1)%}\n\n\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,to_currency,from_currency from `tabCurrency Exchange` where posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n\n\n{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n{% set total_payment_amount = ((sale_transactions |sum(attribute='payment_amount')) + (sale_transactions |sum(attribute='fee_amount'))) %}\n\n{% if sale_transactions|length > 0%}\n    <h4>Payment Breakdown</h4>\n    \n    <table class=\"col-xs-12\" style = \"font-size:13px\">\n        {% for t in sale_transactions %}\n            <tr>\n                <td class=\"col-xs-8\">{{t.payment_type}}\n                {%if t.fee_amount > 0 %}\n                    <span>(+Fee: {{frappe.utils.fmt_money(t.fee_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}})</span>\n                \n                {%endif%}\n                </td>\n                <td class=\"col-xs-8 text-right\">{{frappe.utils.fmt_money(t.input_amount + (t.fee_amount * t.exchange_rate),currency=t.currency,precision=t.currency_precision )}}</td>\n            </tr>\n        {% endfor %}\n        \n        {% for s in sale_tip %}\n            {% if s.tip_amount > 0%}\n                <tr>\n                    <td class=\"col-xs-8\">{{_(\"Tip Amount\")}}</td>\n                    <td class=\"col-xs-8 text-right\">{{frappe.utils.fmt_money(s.tip_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\n                </tr>\n            {% endif %}\n        {% endfor %}\n    </table>\n    \n    <strong><hr/></strong>\n    <br/>\n    <table class=\"col-xs-12\" style = \"font-size:13px\">\n        {% if sale_transactions |sum(attribute='payment_amount') %}\n        <hr/>\n            <tr>\n                <td class=\"col-xs-8\"><strong>{{_(\"Total Payment\")}}</strong></td>\n                <td class=\"col-xs-8 text-right\"><strong>{{frappe.utils.fmt_money(total_payment_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}\n                <br/>\n               = {{frappe.utils.fmt_money(total_payment_amount * exch[0].exchange_rate, currency=second_currency.name, precision=second_currency.custom_currency_precision)}}\n               </strong> </td>\n            </tr>\n         \n        {% endif %}\n    </table>\n{% endif %}"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset on_account = frappe.db.sql(\"\nselect \n    payment_type,\n    currency_symbol,\n    sum(input_amount) as input_amount ,\n    sum(amount) as payment_amount \n    from `tabSale` a\n    inner join `tabPOS Sale Payment` b on b.parent = a.name\n    where working_day = '{}' and payment_type='On Account'\ngroup by\n    currency_symbol,\n    payment_type\".format(doc.name),as_dict=1)\n%}\n\n{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n\n{% if on_account |sum(attribute='payment_amount') %}\n<h4>On Account</h4>\n{% endif %}\n\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    {% if on_account |sum(attribute='payment_amount') %}\n        <tr>\n            <td class=\"col-xs-8\">{{_(\"Total On Account\")}}</td>\n            <td class=\"col-xs-8 text-right\">\n                 {{frappe.utils.fmt_money((on_account |sum(attribute='payment_amount')), currency=main_currency.name, precision=main_currency.custom_currency_precision)}}\n            </td>\n        </tr>\n    {% endif %}\n</table>\n"}]",
    "print_format_builder": 1,
    "print_format_builder_beta": 0,
    "doctype": "Print Format"
}

