    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<center>\n    <h4>{{_(\"Cashier Inventory Transaction\")}}</h4>\n    <h5>{{doc.name}}</h5>\n</center>"}, {"fieldtype": "Section Break", "label": "Shift Information"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n \n{% set from = main %}\n{% set to = second %}\n{% if exch[0].from_currency != main.name %}\n    {% set  from = second%}\n    {% set to = main %}\n{% endif %}\n\n\n\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Working Day:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.working_day}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Business Branch:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.business_branch}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Outlet:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.outlet}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"POS Profile:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.pos_profile}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Created Date:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc.creation,{'fieldtype':'Datetime'})}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Opened By:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{ frappe.get_doc(\"User\", doc.owner).full_name}}\n    </div>\n</div>\n{%if doc.is_closed %}\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Status\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{_(\"Closed\")}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Closed Date:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc.modified,{'fieldtype':'Datetime'})}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Closed By:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{ frappe.get_doc(\"User\", doc.modified_by).full_name}}\n    </div>\n</div>\n{%endif%}\n\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Exchange Rate:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n    {{frappe.utils.fmt_money(1,currency=exch[0].from_currency, precision= from.custom_currency_precision)}} \n    = \n    {{frappe.utils.fmt_money(exch[0].exchange_rate_input,currency=exch[0].to_currency, precision=to.custom_currency_precision)}}\n\n    </div>\n</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%\n    set sql=\"select \n        name, \n        product_name_en,\n        unit,\n        (select sum(quantity) from `tabSale Product` sp inner join `tabSale` s on s.name = sp.parent where s.cashier_shift='{0}' and sp.product_code = p.name  and s.docstatus=1 ) as quantity_sold,\n        (select sum(sp.quantity) from `tabStock Location Product` sp where   sp.product_code = p.name and sp.stock_location = 'SR - Main Stock' ) as balance\n        from `tabProduct` p where is_inventory_product=1 AND p.product_category = if('{1}' != '', '{1}',p.product_category )\"\n   \n    \n%}\n{%\n set data = frappe.db.sql(sql.format(doc.name,frappe.form_dict.get(\"product_category\")), as_dict=1)\n%}\n\n<table class=\"table table-bordered report-list\">\n    <thead>\n    <tr>\n        <th>Product Name</th>\n        <th class=\"text-center\">Unit</th>\n        <th class=\"text-center\">On Hand</th>\n        <th class=\"text-center\">Sale</th>\n        <th class=\"text-center\">Balance</th>\n    </tr>\n    {% for d in data %}\n    <tr>\n        <td>{{d.name}} - {{d.product_name_en}}</td>\n        <td class=\"text-center\">{{d.unit or \"\"}}</td>\n        <td class=\"text-center\">{{(d.quantity_sold or 0) + (d.balance or 0)}}</td>\n        <td class=\"text-center\">{{d.quantity_sold or 0}}</td>\n        <td class=\"text-center\">{{d.balance or 0}}</td>\n    </tr>\n{%endfor%}\n    \n</table>"}]",
