  "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<h4 class=\"text-center\">{{_(\"Purchase Order\")}}</h4>\n<div class=\"text-center\" style=\"text-transform: uppercase;\">\n    {% if doc.docstatus == 1 %}\n        <span>\n        {% if doc.balance == 0 %}\n            <span>Paid</span>\n        {% endif %}\n        {% if doc.total_paid > 0 and doc.balance > 0 %}\n            <span>Partially Paid</span>\n        {% endif %}\n        {% if doc.total_paid == 0 %}\n            <span>Unpaid</span>\n        {% endif %}\n        </span>\n    {% endif %}\n</div>\n<div class=\"row\">\n    {% set vendor = frappe.get_doc('Vendor',doc.vendor,['*']) %}\n    {% if vendor %}\n    <div class=\"col-xs-4\">\n        <table class=\"tbl-list\">\n            <tr>\n                <td>Vendor Code</td>\n                <td>:</td>\n                <td>{{vendor.name}}</td>\n            </tr>\n            <tr>\n                <td>Vendor</td>\n                <td>:</td>\n                <td>{{vendor.vendor_name}}</td>\n            </tr>\n            {% if vendor.company %}\n            <tr>\n                <td>Company</td>\n                <td>:</td>\n                <td>{{vendor.company}}</td>\n            </tr>\n            {% endif %}\n            {% if vendor.phone_number %}\n            <tr>\n                <td>Phone Number</td>\n                <td>:</td>\n                <td>{{vendor.phone_number}}</td>\n            </tr>\n            {% endif %}\n        </table>\n    </div>\n    {% endif %}\n    <div class=\"col-xs-4\">\n        \n    </div>\n    <div class=\"col-xs-4\">\n        <table class=\"tbl-list ml-auto tbl-list-right\">\n            <tr>\n                <td>PO #</td>\n                <td>:</td>\n                <td>{{doc.name}}</td>\n            </tr>\n            {% if doc.referance %}\n            <tr>\n                <td>Ref #</td>\n                <td>:</td>\n                <td>{{doc.referance}}</td>\n            </tr>\n            {% endif %}\n            <tr>\n                <td>Date</td>\n                <td>:</td>\n                <td>{{doc.posting_date}}</td>\n            </tr>\n            <tr>\n                <td>Branch</td>\n                <td>:</td>\n                <td>{{doc.business_branch}}</td>\n            </tr>\n            <tr>\n                <td>Stock Location</td>\n                <td>:</td>\n                <td>{{doc.stock_location}}</td>\n            </tr>\n        </table>\n    </div>\n</div>"}, {"fieldtype": "Section Break", "label": "Product List"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<table class=\"table table-bordered report-list\">\n    <thead>\n        <tr>\n            <th>No</th>\n            <th>Description</th>\n            <th class=\"text-center\">QTY</th>\n            <th class=\"text-center\">Unit</th>\n            <th class=\"text-right\">Cost</th>\n            <th class=\"text-right\">Amount</th>\n        </tr>\n    </thead>\n    <tbody>\n         {% for p in doc.purchase_order_products %}\n        <tr>\n            <td>{{p.idx}}</td>\n            <td>{{p.product_code}} - {{p.product_name}}</td>\n            <td class=\"text-center\">{{p.quantity}}</td>\n            <td class=\"text-center\">{{p.unit}}</td>\n            <td class=\"text-right\">{{ frappe.utils.fmt_money(p.cost,currency=\"$\") }}</td>\n            <td class=\"text-right\">{{frappe.utils.fmt_money(p.amount,currency=\"$\")}}</td>\n        </tr>\n        {% endfor %}\n    </tbody>\n</table>"}, {"fieldtype": "Section Break", "label": "Summary"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class=\"row\">\n    <div class=\"col-xs-6\">\n        {% set payments = frappe.get_list('Purchase Order Payment',filters={\n        'purchase_order': doc.name,'docstatus':1},fields=['*'],order_by='posting_date asc') %}\n        {% if payments %}\n            {% for payment in payments %}\n            <p>Paid on <b>{{payment.posting_date}}</b> by {{payment.payment_type}} : <b>{{frappe.utils.fmt_money(payment.payment_amount, currency=\"$\")}}</b></p>\n            {% endfor %}\n        {% endif %}\n    </div>\n    <div class=\"col-xs-6\">\n        <div class=\"tbl-list-summary\">\n    <table class=\"ml-auto\">\n        <tr>\n            <td>Total Quantity</td>\n            <td>:</td>\n            <td>{{doc.total_quantity}}</td>\n        </tr>\n        <tr>\n            <td>Sub Total</td>\n            <td>:</td>\n            <td>{{ frappe.utils.fmt_money(doc.sub_total,currency=\"$\") }}</td>\n        </tr>\n        {% if doc.total_discount > 0 %}\n        <tr>\n            <td>Total Discount</td>\n            <td>:</td>\n            <td>{{ frappe.utils.fmt_money(doc.total_discount,currency=\"$\") }}</td>\n        </tr>\n        {% endif %}\n        <tr>\n            <td>Grand Total</td>\n            <td>:</td>\n            <td>{{ frappe.utils.fmt_money(doc.grand_total,currency=\"$\") }}</td>\n        </tr>\n        <tr class=\"font-bold\">\n            <td>Total Paid</td>\n            <td>:</td>\n            <td>{{frappe.utils.fmt_money(doc.total_paid,currency=\"$\")}}</td>\n        </tr>\n        {% if doc.balance > 0 %}\n        <tr class=\"font-bold\">\n            <td>Balance</td>\n            <td>:</td>\n            <td>{{frappe.utils.fmt_money(doc.balance,currency=\"$\")}}</td>\n        </tr>\n        {% endif %}\n    </table>\n</div>\n    </div>\n</div>\n"}]",
