.parent\r\n        where\r\n            s.docstatus = 1 and \r\n            s.is_foc = 0 and\r\n            {}\r\n        group by\r\n            sp.revenue_group\r\n    \".format(filter)\r\n%}\r\n{%\r\n    set data = frappe.db.sql(sql,as_dict=1)\r\n%}\r\n\r\n{%\r\n    set sql =\"\r\n        select \r\n            p.payment_type,\r\n            p.currency,\r\n            p.exchange_rate,\r\n            sum(p.input_amount) as input_amount,\r\n            sum(p.payment_amount) as payment_amount,\r\n            sum(p.fee_amount) as fee_amount\r\n        from `tabSale Payment` p \r\n            inner join `tabSale` s on s.name = p.sale\r\n        where\r\n            s.docstatus = 1 and \r\n            s.is_foc = 0 and\r\n            {}\r\n        group by\r\n            p.payment_type,\r\n            p.currency,\r\n            p.exchange_rate\r\n    \".format(filter) \r\n%}\r\n{%\r\n    set data_payments = frappe.db.sql(sql,as_dict=1)\r\n%}\r\n\r\n{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n\r\n<div class=\"container-fluid p-0\" >\r\n    <div class=\"row\">\r\n        <div class=\"col-sm-6 col-12\">\r\n          <div class=\"card mb-4\">\r\n          <div class=\"card-body\">\r\n            <h5 class=\"card-subtitle\">{{_(\"Summary by Revenue Group\")}}</h5>\r\n            <div class=\"pt-2\">\r\n                <table class=\"table table-hover\">\r\n                    <thead>\r\n                      <tr>\r\n                          <th>{{_(\"Revenue Group\")}}</th>\r\n                          <th class=\"text-center\">{{_(\"Quantity\")}}</th>\r\n                          <th class=\"text-end text-right\">{{_(\"Amount\")}}</th>\r\n                      </tr>\r\n                    </thead>\r\n                    <tbody> \r\n                      {% for d in data %}\r\n                      <tr>\r\n                          <td>{{d.revenue_group}}</td>\r\n                          <td class=\"text-center\">{{d.total_quantity}}</td>\r\n                          <td class=\"text-right text-end\">{{frappe.format_value(d.total_revenue,\"Currency\")}}</td>\r\n                      </tr>\r\n                      {% else %}\r\n                      <tr>\r\n                          <td class=\"empty\" colspan=\"3\">No record found</td>\r\n                      </tr>\r\n                      {% endfor %} \r\n                      </tbody>\r\n                </table>\r\n            </div>    \r\n        \r\n          </div>\r\n</div>\r\n        </div>\r\n        <div class=\"col-sm-6 col-12\">\r\n          <div class=\"card mb-4\">\r\n          <div class=\"card-body\">\r\n            <h5 class=\"card-subtitle\">{{_(\"Summary by Payment Type\")}}</h5>\r\n            <div class=\"pt-2\">\r\n                <table class=\"table table-hover\">\r\n                    <thead>\r\n                      <tr>\r\n                          <th>{{_(\"Payment Type\")}}</th>\r\n                          <th class=\"text-end\">{{_(\"Amount\")}}</th>\r\n                      </tr>\r\n                    </thead>\r\n                    <tbody>\r\n                        {% for d in data_payments %}\r\n                          <tr>\r\n                              <td>{{d.payment_type}}\r\n                              {% if d.fee_amount > 0 %}\r\n                                 <span>(+Fee: {{frappe.utils.fmt_money(d.fee_amount, currency=main_currency.name, precision=main_currency.currency_precision)}})</span>\r\n                              {% endif %}\r\n                              </td>\r\n                              \r\n                              \r\n                              <td class=\"text-end text-right\">\r\n                                  {% if d.payment_amount != d.input_amount %}\r\n                                    {{frappe.utils.fmt_money(d.input_amount + (d.fee_amount * d.exchange_rate),currency=d.currency)}}\r\n                                    \r\n                                     - \r\n                                  {% endif %} \r\n                                  {{frappe.utils.fmt_money(d.payment_amount + d.fee_amount,currency=main_currency.currency)}}</td>\r\n                          </tr>\r\n                          {% else %}\r\n                      <tr>\r\n                          <td class=\"empty\" colspan=\"2\">No record found</td>\r\n                      </tr>\r\n                        {% endfor %}\r\n                    </tbody>\r\n                </table>\r\n            </div>    \r\n        \r\n          </div>\r\n        </div>\r\n        </div>\r\n    </div>\r\n</div>"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%\r\n    set filter=\"\r\n    s.working_day = if('{0}'='',s.working_day,'{0}') and \r\n    s.cashier_shift = if('{1}'='',s.cashier_shift,'{1}') and \r\n    ifnull(s.outlet,'') = if('{2}'='',ifnull(s.outlet,''),'{2}') and\r\n    ifnull(s.tbl_group,'') = if('{3}'='',ifnull(s.tbl_group,''),'{3}')\r\n    \".format(\r\n        (frappe.form_dict.get(\"working_day\") or \"\"),\r\n        (frappe.form_dict.get(\"cashier_shift\") or \"\"),\r\n        (frappe.form_dict.get(\"outlet\") or \"\"),\r\n        (frappe.form_dict.get(\"table_group\") or \"\"),\r\n    )\r\n    \r\n%}\r\n{%\r\n    set sql_sale_type =\"\r\n        select \r\n            s.sale_type,\r\n            sum(s.grand_total) as total_revenue,\r\n            sum(s.total_quantity) as total_quantity\r\n        from `tabSale` s  \r\n        where\r\n            s.docstatus = 1 and \r\n            s.is_foc = 0 and\r\n            {}\r\n        group by\r\n            s.sale_type\r\n    \".format(filter)\r\n%}\r\n{%\r\n    set sale_types = frappe.db.sql(sql_sale_type,as_dict=1)\r\n%}\r\n\r\n{%\r\n    set sql_product_group =\"\r\n        select \r\n            sp.product_group,\r\n            sum(sp.total_revenue) as total_revenue,\r\n            sum(sp.quantity) as total_quantity\r\n