        <td>{{frappe.format(t.from_date,{'fieldtype':'Date'}) or ''}}</td>\n        <td align=\"center\">{{t.total_day or ''}}</td>\n        <td>{{t.tour_package_title or ''}}</td>\n        <td>{{t.vehicle_type or ''}}</td>\n        <td align=\"center\">{{t.plate_number or ''}}</td>\n        <td align=\"center\">{{t.quantity or ''}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(t.rate,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(t.total_amount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% endfor %}\n    <tr>\n        <td colspan=\"8\" align=\"right\">{{_(\"Total Amount\")}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_transportation_amount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n</table>\n{% endif %}"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n{% if doc.additional_charges %}\n<div style=\"padding-top:15px\"></div>\n<div>{{_(\"Miscellaneous Charge Detail: \")}}</div>\n<table class = \"report-list\" style=\"width:650px\">\n    <tr>\n        <td align=\"center\">{{_(\"No.\")}}</td>\n        <td align=\"center\" style=\"width:80px\">{{_(\"Date\")}}</td>\n        <td align=\"center\">{{_(\"Invoice No.\")}}</td>\n        <td align=\"center\">{{_(\"Item Charge\")}}</td>\n        <td align=\"center\">{{_(\"Service Name\")}}</td>\n        <td align=\"center\">{{_(\"QTY\")}}</td>\n        <td align=\"center\" style=\"width:85px\">{{_(\"Price\")}}</td>\n        <td align=\"center\" style=\"width:85px\">{{_(\"Total Amount\")}}</td>\n    </tr>\n    {% for a in doc.additional_charges %}\n    <tr>\n        <td align=\"center\">{{loop.index}}</td>\n        <td>{{frappe.format(a.transaction_date,{'fieldtype':'Date'}) or ''}}</td>\n        <td>{{a.reference or ''}}</td>\n        <td>{{a.item_charge or ''}}</td>\n        <td>{{a.decription or ''}}</td>\n        <td align=\"center\">{{a.quantity or ''}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(a.price,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(a.amount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% endfor %}\n    <tr>\n        <td colspan=\"7\" align=\"right\">{{_(\"Total Amount\")}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_additional_charge,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n</table>\n{% endif %}"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>\n    <h4>Payment Detail:</h4>\n    \n</div>\n\n{% set branch = frappe.get_doc(\"Business Branch\",doc.branch) %}\n\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Bank Name\")}}</td>\n        <td>:</td>\n        <td>{{branch.bank_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Account Name\")}}</td>\n        <td>:</td>\n        <td>{{branch.account_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Account Number\")}}</td>\n        <td>:</td>\n        <td>{{branch.bank_account_number or ''}}</td>\n    </tr>\n    \n    <tr>\n        <td>{{_(\"Swift Code\")}}</td>\n        <td>:</td>\n        <td>{{branch.swift_code or ''}}</td>\n    </tr>\n</table>\n\n"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n<table class = \"t-list\" style = \"margin-left:30px\">\n    <tr>\n        <td style = \"width:400px\">{{_(\"Total Tour Package Booking\")}}</td>\n        <td> : </td>\n        <td align=\"right\" style = \"width:170px\">{{frappe.utils.fmt_money(doc.total_tour_package_price,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Total Hotel Booking\")}}</td>\n        <td> : </td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_hotel_amount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n     <tr>\n        <td>{{_(\"Total Restaurant Booking \")}}</td>\n        <td> : </td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_restaurant_amount,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Total Tour Guides Booking \")}}</td>\n        <td> : </td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_tour_guide_amount,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Total Transportation Booking \")}}</td>\n        <td> : </td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_transportation_amount,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Total Miscellaneous \")}}</td>\n        <td> : </td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_additional_charge,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n    </tr>\n</table>"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<div style=\"padding-top:10px\"></div>\r\n<table class = \"t-list\" style = \"margin-left:33px\">\r\n    \r\n{% if doc.is_include_tax %}\r\n    {% set total = doc.total_tour_package_price+doc.total_hotel_amount+doc.total_restaurant_amount+doc.total_tour_guide_amount+doc.total_transportation_amount+doc.total_additional_charge %}\r\n    {% set vat = (doc.vat / 100) + 1 %}\r\n    {% set total_before_tax = (total/vat)+doc.total_discount %}\r\n    {% set grand_total = total_before_tax - doc.total_discount + (total_before_tax - doc.total_discount)*(vat - 1) %}\r\n    \r\n    <tr>\r\n        <td style = \"width:500px\">{{_(\"Grand Total\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\" style = \"width:170px\">{{frappe.utils.fmt_money(grand_total - doc.total_discount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Discount\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_discount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% for type_of_payment in doc.tour_booking_payment | map(attribute='type_of_payment') | unique %}\r\n    <tr>\r\n        <td>{{type_of_payment}}</td>\r\n        <td> : </td>\r\n        <td align=\"right\" style = \"width:140px\">{{frappe.utils.fmt_money(doc.tour_booking_payment|selectattr(\"type_of_payment\", \"equalto\", type_of_payment)|sum(attribute='payment_amount'),currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% endfor %}\r\n    <tr>\r\n        <td>{{_(\"Paid \")}}</td>\r\n        <td> : </td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_paid,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Balance\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.balance,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    \r\n    {% else %}\r\n    {% set total = doc.total_tour_package_price+doc.total_hotel_amount+doc.total_restaurant_amount+doc.total_tour_guide_amount+doc.total_transportation_amount+doc.total_additional_charge %}\r\n    {% set vat = (doc.vat / 100) + 1 %}\r\n    {% set total_before_tax = (total/vat)+doc.total_discount %}\r\n    {% set grand_total = total_before_tax - doc.total_discount %}\r\n    \r\n    <tr>\r\n        <td style = \"width:500px\">{{_(\"Grand Total\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\" style = \"width:170px\">{{frappe.utils.fmt_money(grand_total - doc.total_discount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Discount\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_discount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    \r\n    {% for type_of_payment in doc.tour_booking_payment | map(attribute='type_of_payment') | unique %}\r\n    <tr>\r\n        <td>{{type_of_payment}}</td>\r\n        <td> : </td>\r\n        <td align=\"right\" style = \"width:140px\">{{frappe.utils.fmt_money(doc.tour_booking_payment|selectattr(\"type_of_payment\", \"equalto\", type_of_payment)|sum(attribute='payment_amount'),currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    {% endfor %}\r\n    <tr>\r\n        <td>{{_(\"Paid \")}}</td>\r\n        <td> : </td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_paid,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Balance\")}}</td>\r\n        <td>:</td>\r\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.balance,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\r\n    </tr>\r\n    \r\n    {% endif %}\r\n</table>"}]",
