    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<center>\n    <h4>{{_(\"Working Day Cash Transaction\")}}</h4>\n    <h5>{{doc.name}}</h5>\n</center>"}, {"fieldtype": "Section Break", "label": "Shift Information"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\r\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n \r\n{% set from = main %}\r\n{% set to = second %}\r\n{% if exch[0].from_currency != main.name %}\r\n    {% set  from = second%}\r\n    {% set to = main %}\r\n{% endif %}\r\n\r\n \r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n    <tr>\r\n        <td>{{_(\"Business Branch\")}}</td>\r\n        <td class=\"text-right\">{{doc.business_branch}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Outlet\")}}</td>\r\n        <td class=\"text-right\">{{doc.outlet}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"POS Profile\")}}</td>\r\n        <td class=\"text-right\">{{doc.pos_profile}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Created Date\")}}</td>\r\n        <td class=\"text-right\">{{frappe.format(doc.creation,{'fieldtype':'Datetime'})}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Opened By\")}}</td>\r\n        <td class=\"text-right\">{{ frappe.get_doc(\"User\", doc.owner).full_name}}</td>\r\n    </tr>\r\n{%if doc.is_closed %}\r\n    <tr>\r\n        <td>{{_(\"Status\")}}</td>\r\n        <td class=\"text-right\">{{_(\"Closed\")}}</td>\r\n    </tr>\r\n    <tr>\r\n        <td>{{_(\"Closed Date\")}}</td>\r\n        <td class=\"text-right\">{{frappe.format(doc.modified,{'fieldtype':'Datetime'})}}</td>\r\n    </tr>\r\n     <tr>\r\n        <td>{{_(\"Closed By\")}}</td>\r\n        <td class=\"text-right\">{{ frappe.get_doc(\"User\", doc.modified_by).full_name}}</td>\r\n    </tr>\r\n    \r\n    \r\n    \r\n{%endif%}\r\n\r\n <tr>\r\n        <td>{{_(\"Exchange Rate\")}}</td>\r\n        \r\n        <td class=\"text-right\">\r\n         {{frappe.utils.fmt_money(1,currency=exch[0].from_currency, precision= from.custom_currency_precision)}} \r\n        = \r\n        {{frappe.utils.fmt_money(exch[0].exchange_rate_input,currency=exch[0].to_currency, precision=to.custom_currency_precision)}}\r\n\r\n        </td>\r\n    </tr>\r\n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\r\n{% \r\n    set sale_transactions = frappe.db.sql(\"\r\n                select \r\n                    ct.transaction_status, \r\n                    ct.input_amount,\r\n                    ct.amount,\r\n                    case when ct.transaction_status = 'Cash Out' then ct.amount * -1 else ct.amount end as amount_transaction,\r\n                    ct.payment_type, \r\n                    ct.note,\r\n                    ct.symbol,\r\n                    ct.currency,\r\n                    c.custom_currency_precision\r\n                from `tabCash Transaction` ct\r\n                inner join `tabCurrency` c on c.name = ct.currency\r\n                where \r\n                    ct.working_day = '{}'\r\n    \".format(doc.name),as_dict=1)\r\n%}\r\n\r\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n \r\n\r\n<table class=\"table table-bordered report-list\">\r\n    <tr>\r\n        \r\n        <td><strong>{{_(\"No.\")}}</strong></td>\r\n        \r\n        <td class=\"text-left\"><strong>{{_(\"Status\")}}</strong></td>\r\n    \r\n        <td class=\"text-left\"><strong>{{_(\"Type\")}}</strong></td>\r\n     \r\n        <td class=\"text-left\"><strong>{{_(\"Note\")}}</strong></td>\r\n        \r\n        <td class=\"text-right\"><strong>{{_(\"Amount\")}}</strong></td>\r\n    </tr>\r\n\r\n        {% for transaction_status in sale_transactions | map(attribute = 'transaction_status') | unique %}\r\n            {% for s in sale_transactions | selectattr('transaction_status', 'equalto', transaction_status)%}\r\n                <tr>\r\n                    <td>{{loop.index}}</td>\r\n                    <td>{{s.transaction_status}}</td>\r\n                    <td>{{s.payment_type}}</td>\r\n                    <td>{{s.note}}</td>\r\n                    <td class='text-right'>\r\n                        {{frappe.utils.fmt_money(s.input_amount,currency=s.currency, precision= s.custom_currency_precision)}} \r\n                    </td>\r\n                </tr>\r\n            {% endfor %}\r\n            <tr><td></td></tr>\r\n            <tr>\r\n                <td colspan='3' class='text-right'><strong>{{_(\"Total\")}} {{transaction_status}}</strong></td>\r\n                <td>:</td>\r\n                <td class='text-right'><strong>\r\n                    {{frappe.utils.fmt_money((sale_transactions|selectattr('transaction_status','equalto',transaction_status)|sum(attribute='amount')),currency=main.name, precision=main.custom_currency_precision)}} \r\n                </strong></td>\r\n            </tr>\r\n        {% endfor %}\r\n        <tr><td></td></tr>\r\n        <tr>\r\n            <td colspan='3' class='text-right'><strong>{{_(\"Total Amount\")}}</strong></td>\r\n            <td>:</td>\r\n            <td class='text-right'><strong>\r\n                {{frappe.utils.fmt_money((sale_transactions|sum(attribute='amount_transaction')),currency=main.name, precision=main.custom_currency_precision)}} \r\n            </strong></td>\r\n        </tr>\r\n</table>\r\n"}]",
    "print_format_builder": 1,
    "print_format_builder_beta": 0,
    "doctype": "Print Format"
}

