    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<center>\n    <h4 onclick=\"window.parent.postMessage('123456', '*')\">{{_(\"Close Cashier Shift Summary\")}}</h4>\n    <h5>{{doc.name}}</h5>\n</center>"}, {"fieldtype": "Section Break", "label": "Shift Information"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n \n{% set from = main %}\n{% set to = second %}\n{% if exch[0].from_currency != main.name %}\n    {% set  from = second%}\n    {% set to = main %}\n{% endif %}\n\n\n\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Working Day:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.working_day}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Business Branch:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.business_branch}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Outlet:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.outlet}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"POS Profile:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.pos_profile}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Created Date:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc.creation,{'fieldtype':'Datetime'})}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Opened By:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{ frappe.get_doc(\"User\", doc.owner).full_name}}\n    </div>\n</div>\n{%if doc.is_closed %}\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Status\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{_(\"Closed\")}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Closed Date:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc.modified,{'fieldtype':'Datetime'})}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Closed By:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{ frappe.get_doc(\"User\", doc.modified_by).full_name}}\n    </div>\n</div>\n{%endif%}\n\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Exchange Rate:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n    {{frappe.utils.fmt_money(1,currency=exch[0].from_currency, precision= from.custom_currency_precision)}} \n    = \n    {{frappe.utils.fmt_money(exch[0].exchange_rate_input,currency=exch[0].to_currency, precision=to.custom_currency_precision)}}\n\n    </div>\n</div>"}, {"fieldtype": "Section Break", "label": "Transaction"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \r\nset sale_transactions = frappe.db.sql(\"\r\nselect  sale_type, count(name) as total_transaction , count(name) as total_display from `tabSale` \r\nwhere cashier_shift = '{0}' and docstatus = 1 and is_foc=0 group by sale_type\r\nunion \r\nselect  'FOC', count(name) as total_transaction, count(name) as total_display from `tabSale` where cashier_shift = '{0}' and docstatus = 1 and is_foc=1\r\nunion\r\nselect   'Total Guest Cover' as sale_type, 0 as total_display, sum(guest_cover)  as total_transaction from `tabSale` \r\nwhere cashier_shift = '{0}' and docstatus = 1 \r\n\r\n\".format(doc.name),as_dict=1)\r\n\r\n%}\r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n    <tr>\r\n        <td class=\"col-xs-5\">{{_(\"# Transactions\")}}</td>\r\n        <td class=\"col-xs-8 text-right\">{{sale_transactions |sum(attribute='total_transaction')}}</td>\r\n    </tr>\r\n{% for t in sale_transactions %}\r\n{% if t.total_transaction or t.total_display %}\r\n    <tr>\r\n        <td>{{t.sale_type}}</td>\r\n        <td class=\"text-right\">{{t.total_display}}</td>\r\n    </tr>\r\n{% endif %}\r\n{% endfor %}\r\n</table>\r\n"}, {"fieldtype": "Section Break", "label": "Cash Drawer"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n{% for t in doc.cash_float| selectattr(\"payment_type_group\", \"equalto\", 'Cash')  %}\r\n    <tr>\r\n        <td class=\"col-xs-5\">{{_(\"Starting Amount\")}} ({{t.currency}})</td>\r\n        <td class=\"col-xs-8 text-right\">{{frappe.utils.fmt_money(t.input_amount,currency=t.currency,precision=t.currency_precision)}}</td>\r\n    </tr>\r\n{% endfor %}\r\n</table>\r\n\r\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<hr/>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \r\nset cash_transactions = frappe.db.sql(\"\r\n    select \r\n        ct.transaction_status,\r\n        ct.currency,\r\n        ct.symbol,\r\n        c.custom_currency_precision,\r\n    sum(ct.input_amount) as total_amount\r\n    from `tabCash Transaction` ct\r\n    inner join `tabCurrency` c on c.name = ct.currency\r\n    where cashier_shift = '{}'\r\n    group by\r\n        ct.transaction_status,\r\n        ct.currency,\r\n        ct.symbol,\r\n        c.custom_currency_precision\r\n    \r\n\".format(doc.name),as_dict=1)\r\n%}\r\n\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n \r\n \r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n    <tr>\r\n        <td class=\"col-xs-7\">{{_(\"Total Opening Amount\")}}</td>\r\n        <td class=\"col-xs-7 text-right\">\r\n            {{frappe.utils.fmt_money(doc.total_opening_amount,currency=main.name, precision= main.custom_currency_precision)}} \r\n        </td>\r\n    </tr>\r\n<!--Start Render Cash In/Out-->\r\n{% for t in cash_transactions %}\r\n    <tr>\r\n        <td>{{_(t.transaction_status)}} ({{t.currency}})</td>\r\n        <td class=\"text-right\">\r\n            {{frappe.utils.fmt_money(t.total_amount,currency=t.currency,  precision= t.custom_currency_precision)}}\r\n        </td>\r\n    </tr>\r\n {% endfor %}\r\n <!--End Render Cash In/Out-->\r\n \r\n <!--Start Render Cash Sale-->\r\n {% \r\n    set cash_sale = frappe.db.sql(\"\r\n    select \r\n        sum(payment_amount) as payment_amount \r\n    from `tabSale Payment`\r\n    where cashier_shift = '{}' and payment_type_group='Cash'\".format(doc.name),as_dict=1)\r\n    %}\r\n    <tr>\r\n        <td>{{_(\"Total Cash Sale\")}} ({{frappe.db.get_default(\"currency\")}})</td>\r\n        <td class=\"text-right\">\r\n            {{frappe.utils.fmt_money(cash_sale[0].payment_amount,currency=main.name,  precision= main.custom_currency_precision)}}\r\n        </td>\r\n    </tr>\r\n    \r\n <!--Start render close amount-->\r\n \r\n{% for t in doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash') %}\r\n{% if t.input_close_amount %}\r\n    <tr>\r\n        <td>{{_(\"Close Amount\")}} ({{t.currency}})</td>\r\n        <td class=\"text-right\">\r\n            {{frappe.utils.fmt_money(t.input_close_amount,currency=t.currency,  precision= t.currency_precision)}}\r\n        </td>\r\n    </tr>\r\n{% endif %}\r\n{% endfor %}\r\n</table>\r\n\r\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% if doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash')  | sum(attribute='close_amount') %}\n<hr/>\n{% endif %}"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \r\nset cash_transactions = frappe.db.sql(\"\r\n    select \r\n        ct.transaction_status,\r\n        ct.currency,\r\n        ct.symbol,\r\n        c.custom_currency_precision,\r\n    sum(ct.input_amount) as total_amount\r\n    from `tabCash Transaction` ct\r\n    inner join `tabCurrency` c on c.name = ct.currency\r\n    where cashier_shift = '{}'\r\n    group by\r\n        ct.transaction_status,\r\n        ct.currency,\r\n        ct.symbol\r\n    \r\n\".format(doc.name),as_dict=1)\r\n%}\r\n\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\r\n\r\n<table class=\"col-xs-12\" style = \"font-size:13px\">\r\n{% if doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash')  | sum(attribute='close_amount') %}\r\n    <tr>\r\n        <td class=\"col-xs-5\">{{_(\"Total Closed Amount\")}} ({{frappe.db.get_default(\"Currency\")}})</td>\r\n         <td class=\"col-xs-8 text-right\">\r\n            {{frappe.utils.fmt_money((doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash') | sum(attribute='close_amount')),currency=main.name, precision= main.custom_currency_precision)}} \r\n        </td>\r\n    </tr>\r\n{% endif %}\r\n\r\n{% if doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash') | sum(attribute='system_close_amount') %}\r\n    <tr>\r\n        <td>{{_(\"Expected Cash\")}}</td>\r\n        <td class=\"text-right\">\r\n            {{frappe.utils.fmt_money((doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash') | sum(attribute='system_close_amount')),currency=main.name, precision= main.custom_currency_precision)}} \r\n        </td>\r\n    </tr>\r\n{% endif %}\r\n\r\n{% if doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash') | sum(attribute='different_amount')%}\r\n    <tr>\r\n        <td>{{_(\"Different Amount\")}} ({{frappe.db.get_default(\"Currency\")}})</td>\r\n        <td class=\"text-right\">\r\n            {{frappe.utils.fmt_money((doc.cash_float | selectattr(\"payment_type_group\", \"equalto\", 'Cash') | sum(attribute='different_amount')),currency=main.name, precision= main.custom_currency_precision)}} \r\n        </td>\r\n    </tr>\r\n{% endif %}\r\n</table>\r\n\r\n"}, {"fieldtype": "Section Break", "label": "Sale Summary"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\r\n{% \r\n    set sale_transactions = frappe.db.sql(\"select \r\n         coalesce(sum(if(is_foc=0,sub_total,0)),0) as sub_total,\r\n        coalesce(sum(if(is_foc=0,total_discount,0)),0) as total_discount,\r\n        coalesce(sum(if(is_foc=0,tax_1_amount,0)),0) as tax_1,\r\n        coalesce(sum(if(is_foc=0,tax_2_amount,0)),0) as tax_2,\r\n        coalesce(sum(if(is_foc=0,tax_3_amount,0)),0) as tax_3,\r\n        coalesce(sum(if(is_foc = 1,grand_total,0)),0) as total_foc_amount,\r\n        coalesce(sum(if(is_foc = 0,grand_total,0)),0) as grand_total,\r\n        coalesce(sum(commission_amount),0) as commission_amount,\r\n        coalesce(sum(changed_amount),0) as changed_amount\r\n    from `tabSale`\r\n    where \r\n        cashier_shift = '{}' and \r\n        docstatus=1\r\n    \".format(doc.name),as_dict=1)\r\n%}\r\n\r\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\r\n\r\n{% for t in sale_transactions %}\r\n    <table class=\"col-xs-12\" style = \"font-size:13px\">\r\n        <tr>\r\n            <td class=\"col-xs-8\">{{_(\"Sale Sub Total\")}}</td>\r\n            <td class=\"col-xs-8 text-right\"> \r\n                 {{frappe.utils.fmt_money(t.sub_total,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% if t.total_discount %}\r\n        <tr>\r\n            <td>{{_(\"Total Discount\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.total_discount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% endif %}\r\n        <tr>\r\n            <td>{{_(\"Total Net Sale\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.grand_total,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% if t.tax_1 %}\r\n        <tr>\r\n            <td>{{_(\"Tax 1\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.tax_1,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% endif %}\r\n    {% if t.tax_2 %}\r\n        <tr>\r\n            <td>{{_(\"Tax 2\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.tax_2,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% endif %}\r\n    {% if t.tax_3 %}\r\n        <tr>\r\n            <td>{{_(\"Tax 3\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.tax_3,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% endif %}\r\n        <tr>\r\n            <td>{{_(\"Total Sale Revenue\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.grand_total-t.commission_amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% if t.changed_amount %}\r\n        <tr>\r\n            <td>{{_(\"Total Changed Amount\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.changed_amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% endif %}\r\n    {% if t.total_foc_amount %}\r\n        <tr>\r\n            <td>{{_(\"Total FOC Amount\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.total_foc_amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% endif %}\r\n    {% if t.commission_amount %}\r\n        <tr>\r\n            <td>{{_(\"Total Comission\")}}</td>\r\n            <td class=\"text-right\">\r\n                {{frappe.utils.fmt_money(t.commission_amount,currency=main.name, precision=main.custom_currency_precision)}}\r\n            </td>\r\n        </tr>\r\n    {% endif %}\r\n    </table>\r\n{% endfor %}"}, {"fieldtype": "Section Break", "label": "Sale By Revenue Group"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset sale_transactions = frappe.db.sql(\"\nselect revenue_group,\nsum(total_revenue) as amount from `tabSale Product` sp\ninner join tabSale s on s.name = sp.parent\nwhere s.cashier_shift = '{}' and s.is_foc=0\ngroup by revenue_group\".format(doc.name),as_dict=1)\n%}\n\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n\n{% for t in sale_transactions %}\n{% if t.amount %}\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    <tr>\n        <td class=\"col-xs-5\">{{t.revenue_group}}</td>\n        <td class=\"col-xs-8 text-right\">\n            {{frappe.utils.fmt_money(t.amount,currency=main.name, precision=main.custom_currency_precision)}}\n        </td>\n    </tr>\n</table>\n{% endif %}\n{% endfor %}\n\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset sale_transactions = frappe.db.sql(\"\nselect revenue_group,\nsum(total_revenue) as amount from `tabSale Product` sp\ninner join tabSale s on s.name = sp.parent\nwhere s.cashier_shift = '{}' and s.is_foc=1\ngroup by revenue_group\".format(doc.name),as_dict=1)\n%}\n{% if sale_transactions |sum(attribute='amount') %}\n    <h4>FOC By Revenue Group</h4>\n{% endif %}    \n\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n\n{% for t in sale_transactions %}\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    <tr>\n        <td class=\"col-xs-5\">{{t.revenue_group}}</td>\n        <td class=\"col-xs-8 text-right\">\n            {{frappe.utils.fmt_money(t.sub_total,currency=main.name, precision=main.custom_currency_precision)}}\n        </td>\n    </tr>\n</table>\n{% endfor %}"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset sale_transactions = frappe.db.sql(\"\nselect \n    payment_type,\n    symbol,\n    exchange_rate,\n    currency,\n    currency_precision,\n    sum(fee_amount) as fee_amount,\n    sum(input_amount) as input_amount ,\n    sum(payment_amount) as payment_amount \nfrom `tabSale Payment`\nwhere cashier_shift = '{}'\ngroup by\n    symbol,\n    exchange_rate,\n    currency,\n    currency_precision,\n    payment_type\".format(doc.name),as_dict=1)\n%}\n{% set sale_tip = frappe.db.sql(\"\n                    select \n                        sum(tip_amount) as tip_amount\n                    from `tabSale`\n                    where cashier_shift = '{}'\n\".format(doc.name),as_dict=1)%}\n\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,to_currency,from_currency from `tabCurrency Exchange` where posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n\n\n{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n{% set total_payment_amount = ((sale_transactions |sum(attribute='payment_amount')) + (sale_transactions |sum(attribute='fee_amount'))) %}\n\n<h4>Payment Breakdown</h4>\n\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    {% for t in sale_transactions %}\n        <tr>\n            <td class=\"col-xs-8\">{{t.payment_type}}\n            {%if t.fee_amount > 0 %}\n                <span>(+Fee: {{frappe.utils.fmt_money(t.fee_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}})</span>\n            \n            {%endif%}\n            </td>\n            <td class=\"col-xs-8 text-right\">{{frappe.utils.fmt_money(t.input_amount + (t.fee_amount * t.exchange_rate),currency=t.currency,precision=t.currency_precision )}}</td>\n        </tr>\n    {% endfor %}\n    \n    {% for s in sale_tip %}\n        {% if s.tip_amount > 0%}\n            <tr>\n                <td class=\"col-xs-8\">{{_(\"Tip Amount\")}}</td>\n                <td class=\"col-xs-8 text-right\">{{frappe.utils.fmt_money(s.tip_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}</td>\n            </tr>\n        {% endif %}\n    {% endfor %}\n</table>\n\n<strong><hr/></strong>\n<br/>\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    {% if sale_transactions |sum(attribute='payment_amount') %}\n    <hr/>\n        <tr>\n            <td class=\"col-xs-8\"><strong>{{_(\"Total Payment\")}}</strong></td>\n            <td class=\"col-xs-8 text-right\"><strong>\n                {{frappe.utils.fmt_money(total_payment_amount, currency=main_currency.name, precision=main_currency.custom_currency_precision)}}\n            <br/>\n           = {{frappe.utils.fmt_money(total_payment_amount * exch[0].exchange_rate, currency=second_currency.name, precision=second_currency.custom_currency_precision)}}\n           </strong> </td>\n        </tr>\n     \n    {% endif %}\n</table>\n\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<hr/>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\n{% \n    set cash_transaction = frappe.db.sql(\"\n                select \n                    ct.transaction_status, \n                    ct.input_amount,\n                    ct.amount,\n                    ct.payment_type, \n                    ct.note,\n                    ct.symbol\n                from `tabCash Transaction` ct\n                where \n                    ct.cashier_shift = '{}'\n    \".format(doc.name),as_dict=1)\n%}\n{% \nset sale_transactions = frappe.db.sql(\"\nselect \n    payment_type,\n    symbol,\n    sum(input_amount) as input_amount ,\n    sum(fee_amount) as fee_amount ,\n    sum(payment_amount) as payment_amount \nfrom `tabSale Payment`\nwhere cashier_shift = '{}'\ngroup by\n    symbol,\n    payment_type\".format(doc.name),as_dict=1)\n%}\n\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,to_currency,from_currency from `tabCurrency Exchange` where posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second= frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    <tr>\n        <td class=\"col-xs-5\"><strong>\n            {{_(\"Total Revenue\")}}\n        </strong></td>\n        <td class='col-xs-8 text-right'><strong>\n            {{frappe.utils.fmt_money((doc.total_opening_amount + sale_transactions |sum(attribute='payment_amount')+sale_transactions |sum(attribute='fee_amount')+cash_transaction|selectattr('transaction_status','equalto',\"Cash In\")|sum(attribute='amount')-cash_transaction|selectattr('transaction_status','equalto',\"Cash Out\")|sum(attribute='amount')), currency=main.name, precision=main.custom_currency_precision)}}\n            <br/>\n            = {{frappe.utils.fmt_money((doc.total_opening_amount + sale_transactions |sum(attribute='payment_amount')+sale_transactions |sum(attribute='fee_amount')+cash_transaction|selectattr('transaction_status','equalto',\"Cash In\")|sum(attribute='amount')-cash_transaction|selectattr('transaction_status','equalto',\"Cash Out\")|sum(attribute='amount')) * exch[0].exchange_rate, currency=second.name, precision=second.custom_currency_precision)}}\n        </strong></td>\n    </tr>\n</table>\n\n"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \nset on_account = frappe.db.sql(\"\nselect \n    payment_type,\n    currency_symbol,\n    sum(input_amount) as input_amount ,\n    sum(amount) as payment_amount \n    from `tabSale` a\n    inner join `tabPOS Sale Payment` b on b.parent = a.name\n    where cashier_shift = '{}' and payment_type='On Account'\ngroup by\n    currency_symbol,\n    payment_type\".format(doc.name),as_dict=1)\n%}\n\n{% if on_account |sum(attribute='payment_amount') %}\n<hr/>\n<h4>On Account</h4>\n{% endif %}\n\n{% if on_account |sum(attribute='payment_amount') %}\n<table class=\"col-xs-12\" style = \"font-size:13px\">\n    <tr>\n        <td class=\"col-xs-5\">{{_(\"Total On Account\")}} ({{frappe.db.get_default(\"Currency\")}})</td>\n         <td class=\"col-xs-8 text-right\">{{frappe.format_value((on_account |sum(attribute='payment_amount')),\"Currency\")}}</td>\n    </tr>\n</table>\n{% endif %}\n"}]",
