    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class=\"print-heading\">\n    <h2>Bus Ticket Booking</h2>\n</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div><h4>{{_(\"BUS COMFIRMATION LETTER\")}}</h4></div>\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Reservation ID\")}}</td>\n        <td>:</td>\n        <td>{{doc.name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Booking Date\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.booking_date,{'fieldtype':'Date'}) or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Date\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.departure,{'fieldtype':'Date'}) or ''}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\n<table class=\"table-no-boder\" style=\"margin-top:40px;margin-left:40px\">\n{% if doc.transportation_company%}\n    {% set transportation = frappe.get_doc(\"Transportation Company\",doc.transportation_company) %}\n    <tr>\n        <td style=\"width:130px\">{{_(\"Company Name\")}}</td>\n        <td>:</td>\n        <td>{{transportation.name or ''}}</td>\n    </tr>\n{% else %}\n    <tr>\n        <td>{{_(\"Company Name\")}}</td>\n        <td>:</td>\n        <td></td>\n    </tr>\n{% endif %}\n    {% if doc.is_round_trip_bus %}\n    <tr>\n        <td>{{_(\"Itineray\")}}</td>\n        <td>:</td>\n        <td>{{_(\"Round Trip\")}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Return Date\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.return_date_bus,{'fieldtype':'Date'}) or ''}}</td>\n    </tr>\n    {% else %}\n    <tr>\n        <td>{{_(\"Itineray\")}}</td>\n        <td>:</td>\n        <td>{{_(\"One Way\")}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Time\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.departure,{'fieldtype':'Time'}) or ''}}</td>\n    </tr>\n    {% endif %}\n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<h4>{{_(\"GUEST DETAIL\")}}</h4>\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Guest Name\")}}</td>\n        <td>:</td>\n        <td>{{doc.guest_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Contact Number\")}}</td>\n        <td>:</td>\n        <td>{{doc.customer_phone_number or ''}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\n<table class=\"table-no-boder\" style=\"margin-top:40px;margin-left:40px\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Nationality\")}}</td>\n        <td>:</td>\n        <td>{{doc.nationality}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style=\"padding-top:15px\"></div>\n<div><h4>{{_(\"TRAVEL COMFIRMATION\")}}</h4></div>\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Reference No.\")}}</td>\n        <td>:</td>\n        <td>{{doc.reference}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Date\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.departure,{'fieldtype':'Date'})}}</td>\n    </tr>\n{% if doc.is_round_trip_bus %}\n    <tr>\n        <td></td>\n    </tr>\n{% else %}\n    <tr>\n        <td>{{_(\"Total Ticket\")}}</td>\n        <td>:</td>\n        <td>{{doc.total_quantity}}</td>\n    </tr>\n{% endif %}\n</table>"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "\n<table class=\"table-no-boder\" style=\"margin-top:55px;margin-left:40px\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Destination\")}}</td>\n        <td>:</td>\n        <td>{{doc.destination or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Time\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.departure,{'fieldtype':'Time'}) or ''}}</td>\n    </tr>\n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% if doc.is_round_trip_bus %}\n<div style=\"padding-top:15px\"></div>\n<div><h4>{{_(\"RETURN BUS\")}}</h4></div>\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Reference No.\")}}</td>\n        <td>:</td>\n        <td>{{doc.reference or ''}}</td>\n    </tr>\n        <tr>\n        <td>{{_(\"Destination\")}}</td>\n        <td>:</td>\n        <td>{{doc.return_destination or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Departure Date\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.return_date_bus,{'fieldtype':'Date'}) or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Total Ticket\")}}</td>\n        <td>:</td>\n        <td>{{doc.total_quantity or ''}}</td>\n    </tr>\n</table>\n{% endif %}"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% if doc.is_round_trip_bus %}\n<table class=\"table-no-boder\" style=\"margin-top:55px;margin-left:40px\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Departure Time\")}}</td>\n        <td>:</td>\n        <td>{{frappe.format(doc.return_date_bus,{'fieldtype':'Time'}) or ''}}</td>\n    </tr>\n</table>\n{% endif %}"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n<div style=\"padding-top:5px\"></div>\n<table class=\"report-list\" style=\"width:650px\">\n    <tr>\n        <td align=\"center\">{{_(\"No.\")}}</td>\n        <td>{{_(\"Ticket Name\")}}</td>\n        <td align=\"center\">{{_(\"Number\")}}</td>\n        <td align=\"center\">{{_(\"Quantity\")}}</td>\n        <td align=\"center\">{{_(\"Price\")}}</td>\n        <td align=\"right\">{{_(\"Total Amount\")}}</td>\n    </tr>\n    {% for t in doc.ticket_booking_item %}\n    <tr>\n        <td align=\"center\">{{loop.index}}</td>\n        <td>{{t.ticket_name or ''}}</td>\n        <td align=\"center\">{{t.ticket_number or ''}}</td>\n        <td align=\"center\">{{t.quantity or ''}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(t.price,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(t.total_amount,currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% endfor %}\n</table>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style=\"padding-top:15px\"></div>\n<h5>{{_(\"Payment Detail:\")}}</h5>\n{% set branch = frappe.get_doc(\"Business Branch\",doc.branch) %}\n<table class=\"table-no-boder\">\n    <tr>\n        <td style=\"width:130px\">{{_(\"Bank Name\")}}</td>\n        <td>:</td>\n        <td>{{branch.bank_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Account Name\")}}</td>\n        <td>:</td>\n        <td>{{branch.account_name or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Account Number\")}}</td>\n        <td>:</td>\n        <td>{{branch.bank_account_number or ''}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Swift Code\")}}</td>\n        <td>:</td>\n        <td>{{branch.swift_code or ''}}</td>\n    </tr>\n</table>\n"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%set main_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second_currency = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n\n<div style=\"padding-top:20px\"></div>\n<table  class = \"t-list\" style=\"margin-left:65px\">\n   {% if doc.is_include_tax %}\n    {% set vat = (doc.vat / 100) + 1 %}\n    {% set total_before_tax = (doc.total_amount/vat)+doc.total_discount %}\n    {% set grand_total = total_before_tax - doc.total_discount + (total_before_tax - doc.total_discount)*(vat - 1) %}\n    \n    <tr>\n        <td width=\"95px\">{{_(\"Grand Total\")}}</td>\n        <td>:</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(grand_total - doc.total_discount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Discount\")}}</td>\n        <td>:</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_discount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% for type_of_payment in doc.payments | map(attribute='type_of_payment') | unique %}\n    <tr>\n        <td>{{type_of_payment}}</td>\n        <td> : </td>\n        <td align=\"right\" style = \"width:140px\">{{frappe.utils.fmt_money(doc.payments|selectattr(\"type_of_payment\", \"equalto\", type_of_payment)|sum(attribute='payment_amount'),currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% endfor %}\n    <tr>\n        <td>{{_(\"Balance\")}}</td>\n        <td>:</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.balance,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% else %}\n    {% set vat = (doc.vat / 100) + 1 %}\n    {% set total_before_tax = (doc.total_amount/vat)+doc.total_discount %}\n    {% set grand_total = total_before_tax - doc.total_discount %}\n    \n    <tr>\n        <td width=\"95px\">{{_(\"Grand Total\")}}</td>\n        <td>:</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(grand_total - doc.total_discount ,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    <tr>\n        <td>{{_(\"Discount\")}}</td>\n        <td>:</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.total_discount,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% for type_of_payment in doc.payments | map(attribute='type_of_payment') | unique %}\n    <tr>\n        <td>{{type_of_payment}}</td>\n        <td> : </td>\n        <td align=\"right\" style = \"width:140px\">{{frappe.utils.fmt_money(doc.payments|selectattr(\"type_of_payment\", \"equalto\", type_of_payment)|sum(attribute='payment_amount'),currency=main_currency.name,precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% endfor %}\n    <tr>\n        <td>{{_(\"Balance\")}}</td>\n        <td>:</td>\n        <td align=\"right\">{{frappe.utils.fmt_money(doc.balance,currency=main_currency.name, precision=main_currency.currency_precision)}}</td>\n    </tr>\n    {% endif %}\n</table>"}]",
