    "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<center>\n    <h4>{{_(\"Cashier Shift Sale Transaction\")}}</h4>\n    <h5>{{doc.name}}</h5>\n</center>"}, {"fieldtype": "Section Break", "label": "Shift Information"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n \n{% set from = main %}\n{% set to = second %}\n{% if exch[0].from_currency != main.name %}\n    {% set  from = second%}\n    {% set to = main %}\n{% endif %}\n\n\n\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Working Day:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.working_day}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Business Branch:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.business_branch}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Outlet:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.outlet}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"POS Profile:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{doc.pos_profile}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Created Date:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc.creation,{'fieldtype':'Datetime'})}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Opened By:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{ frappe.get_doc(\"User\", doc.owner).full_name}}\n    </div>\n</div>\n{%if doc.is_closed %}\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Status\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{_(\"Closed\")}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Closed Date:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc.modified,{'fieldtype':'Datetime'})}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Closed By:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{ frappe.get_doc(\"User\", doc.modified_by).full_name}}\n    </div>\n</div>\n{%endif%}\n\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Exchange Rate:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n    {{frappe.utils.fmt_money(1,currency=exch[0].from_currency, precision= from.custom_currency_precision)}} \n    = \n    {{frappe.utils.fmt_money(exch[0].exchange_rate_input,currency=exch[0].to_currency, precision=to.custom_currency_precision)}}\n\n    </div>\n</div>"}, {"fieldtype": "Section Break", "label": "Sale Transaction"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \n    set sale_transactions = frappe.db.sql(\"select coalesce(sale_type,'Not Set') as sale_type,name,tbl_number,TIME_FORMAT(creation, '%H:%i') as time, total_quantity,grand_total,created_by from `tabSale` where cashier_shift = '{}' and docstatus = 1 and is_foc = 0\".format(doc.name),as_dict=1)\n%}\n\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n \n \n\n<table class=\"table table-bordered report-list\">\n    <thead>\n        \n        <tr>\n        \n        <th>{{_(\"Receipt #\")}}</th>\n   \n        <th class=\"text-center\">{{_(\"Tbl #\")}}</th>\n    \n        <th class=\"text-center\">{{_(\"Time\")}}</th>\n     \n        <th class=\"text-center\">{{_(\"Qty\")}}</th>\n        \n        <th class=\"text-right\">{{_(\"Amt\")}}</th>\n   \n        <th>{{_(\"By\")}}</th>\n\n    </tr>\n    \n    </thead>\n    \n    <tbody>\n        {% for sale_type in sale_transactions | map(attribute='sale_type') | unique  %}\n        <tr>\n            <td colspan=\"6\"><strong>{{sale_type}}</strong></td>\n        </tr>\n           {% for s in sale_transactions |selectattr(\"sale_type\", \"equalto\", sale_type)   %}\n                <tr>\n                    <td><a onclick=\"window.parent.postMessage('view_sale_detail|{{s.name}}', '*')\">{{s.name}}</a></td>\n                    <td class=\"text-center\">{{s.tbl_number}}</td>\n                    <td class=\"text-center\">{{s.time}}</td>\n                    <td class=\"text-center\">{{s.total_quantity}}</td>\n                    <td class='text-right'>\n                        {{frappe.utils.fmt_money(s.grand_total,currency=main.name, precision=main.custom_currency_precision)}} \n                    </td>\n                    <td>{{s.created_by}}</td>\n                </tr>\n           {% endfor %}\n            <tr>\n                 <td colspan=\"1\" class=\"text-right\"><strong>{{_(\"Total\")}} {{sale_type}}</strong></td>\n                <td class=\"text-center\"><strong>{{sale_transactions | selectattr(\"sale_type\", \"equalto\", sale_type) | list | length}}</strong></td>\n                <td></td>\n                <td class=\"text-center\"> <strong>{{sale_transactions |selectattr(\"sale_type\", \"equalto\", sale_type) |sum(attribute='total_quantity')}}</strong></td>\n                <td align=\"right\"><strong>\n                {{frappe.utils.fmt_money(sale_transactions|selectattr(\"sale_type\", \"equalto\", sale_type)| sum(attribute='grand_total'),currency=main.name, precision=main.custom_currency_precision)}} \n                </strong></td>\n            </tr>\n        \n        {% endfor %}\n        \n    </tbody>\n    <tfoot>\n        <tr>\n                <td colspan=\"1\" class=\"text-right\"><strong>{{_(\"Total\")}}</strong></td>\n                <td class=\"text-center\"><strong>{{sale_transactions| count }}</strong></td>\n                <td> </td>\n                <td class=\"text-center\"><strong>{{sale_transactions |sum(attribute='total_quantity')}}</strong></td>\n                <td class=\"text-right\"><strong>\n                    {{frappe.utils.fmt_money(sale_transactions | sum(attribute='grand_total'),currency=main.name, precision=main.custom_currency_precision)}}\n                    <br/>\n                    = {{frappe.utils.fmt_money((sale_transactions | sum(attribute='grand_total')) * exch[0].exchange_rate ,currency=second.name, precision=second.custom_currency_precision)}} \n                </strong></td>\n            </tr>\n    </tfoot>\n</table>\n"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \n    set sale_transactions = frappe.db.sql(\"select sale_type,name,tbl_number,TIME_FORMAT(creation, '%H:%i') as time, total_quantity,grand_total,created_by,customer_name from `tabSale` where cashier_shift = '{}' and docstatus = 1 and is_foc = 1\".format(doc.name),as_dict=1)\n%}\n\n\n\n{% set exch = frappe.db.sql(\"select exchange_rate,exchange_rate_input,from_currency,to_currency from `tabCurrency Exchange` where docstatus = 1 and posting_date <= '{}' and from_currency <> to_currency order by posting_date  desc limit 1\".format(doc.posting_date),as_dict=1) %}\n{%set main = frappe.get_doc(\"Currency\",frappe.db.get_default(\"currency\"))%}\n{%set second = frappe.get_doc(\"Currency\",frappe.db.get_default(\"second_currency\"))%}\n \n \n \n{% if sale_transactions %}\n<h4 class=\"col-sm-12\" style=\"padding-left:0px\">{{_(\"FOC\")}}</h4>\n\n<table class=\"table table-bordered report-list\">\n    <thead>\n        \n        <tr>\n        \n        <th>{{_(\"Receipt #\")}}</th>\n   \n        <th class=\"text-center\">{{_(\"Tbl #\")}}</th>\n    \n        <th class=\"text-center\">{{_(\"Time\")}}</th>\n     \n        <th class=\"text-center\">{{_(\"Qty\")}}</th>\n        \n        <th class=\"text-right\">{{_(\"Amt\")}}</th>\n   \n        <th>{{_(\"Customer\")}}</th>\n\n    </tr>\n    \n    </thead>\n    \n    <tbody>\n        {% for sale_type in sale_transactions | map(attribute='sale_type') | unique  %}\n        <tr>\n            <td colspan=\"6\"><strong>{{sale_type}}</strong></td>\n        </tr>\n           {% for s in sale_transactions |selectattr(\"sale_type\", \"equalto\", sale_type)   %}\n                <tr>\n                    <td><a onclick=\"window.parent.postMessage('view_sale_detail|{{s.name}}', '*')\">{{s.name}}</a></td>\n                    <td class=\"text-center\">{{s.tbl_number}}</td>\n                    <td class=\"text-center\">{{s.time}}</td>\n                    <td class=\"text-center\">{{s.total_quantity}}</td>\n                    <td class='text-right'>\n                        {{frappe.utils.fmt_money(s.grand_total,currency=main.name, precision=main.custom_currency_precision)}} \n                    </td>\n                    <td>{{s.customer_name}}</td>\n                </tr>\n           {% endfor %}\n            <tr>\n                 <td colspan=\"1\" class=\"text-right\"><strong>{{_(\"Total\")}} {{sale_type}}</strong></td>\n                <td class=\"text-center\"><strong>{{sale_transactions | selectattr(\"sale_type\", \"equalto\", sale_type) | list | length}}</strong></td>\n                <td></td>\n                <td class=\"text-center\"> <strong>{{sale_transactions |selectattr(\"sale_type\", \"equalto\", sale_type) |sum(attribute='total_quantity')}}</strong></td>\n                <td align=\"right\"><strong>\n                    {{frappe.utils.fmt_money((sale_transactions|selectattr(\"sale_type\", \"equalto\", sale_type)| sum(attribute='grand_total')),currency=main.name, precision=main.custom_currency_precision)}} \n                </strong></td>\n            </tr>\n        \n        {% endfor %}\n        \n    </tbody>\n    <tfoot>\n        <tr>\n                <td colspan=\"1\" class=\"text-right\"><strong>{{_(\"Total\")}}</strong></td>\n                <td class=\"text-center\"><strong>{{sale_transactions| count }}</strong></td>\n                <td> </td>\n                <td class=\"text-center\"><strong>{{sale_transactions |sum(attribute='total_quantity')}}</strong></td>\n                <td class=\"text-right\"><strong>\n                    \n                    {{frappe.utils.fmt_money(sale_transactions | sum(attribute='grand_total'),currency=main.name, precision=main.custom_currency_precision)}}\n                    <br/>\n                    = {{frappe.utils.fmt_money((sale_transactions | sum(attribute='grand_total')) * exch[0].exchange_rate ,currency=second.name, precision=second.custom_currency_precision)}} \n                </strong></td>\n            </tr>\n    </tfoot>\n</table>\n\n{%endif%}"}]",
