css:
@media print {
  @page {
    margin-top: 30px;
  }
}

.print-heading {
    border-bottom: 1px solid #f4f5f6;
    padding-bottom: 5px;
    margin-bottom: 10px;
}

.report-list tbody tr {
    border-bottom:dotted 1px #e9e9e9!important;
}
.report-list tbody tr.f-tb-child{
    border-bottom:dotted 1px #444444!important;
} 

h2 {
    font-size: 14px;
    text-align:center!important;
    margin:0px!important;
    padding:0px!important;
    text-transform: uppercase!important;
}

h3{
    margin-top:0px!important
}

hr{
    margin-top: 5px;
    margin-bottom: 5px;
    border: 0;
    border-top: 1px solid #000000;
}

.print-heading h2 {
    font-size: 16px;
    text-align:center
}

.print-heading h2 div {
    font-weight: 600;
}


.print-heading small {
    font-size: 12px !important;
    font-weight: normal;
    line-height: 2.5;
    color: #4c5a67;
}

.print-format .letter-head {
    margin-bottom: 5px;
}

.print-format .letter-head h1 {
    margin: 0px!important;
}

.print-format label {
    font-weight: normal;
    font-size: 12px;
    color: #000;
    margin-bottom: 0;
}

.print-format .data-field {
    margin-top: 0;
    margin-bottom: 0;
}

.print-format .value {
    color: #000;
    line-height: 1.8;
}

.print-format .section-break:not(:last-child) {
    margin-bottom: 0;
}

.print-format .row:not(.section-break) {
    line-height: 1.6;
    margin-top: 0px !important;
}

.print-format .important .value {
    font-size: 12px;
    font-weight: 600;
}
.print-format thead {
    border-bottom:solid 1px #000!important;
}
.print-format th {
    color: #000!important;
    font-weight: normal;
    border:0px  !important;
    background:#a2a2a2!important;
    
}

.print-format .table-bordered td {
    border: 0px!important;
    border-bottom: solid 1px #efefef!important;
    padding: 0px 0px!important;

    
}
.print-format .table-bordered td span{
    padding-left: 0px 0px!important;
}

.print-format .table-bordered tr {
    height:25px!important;
}

.print-format .table-bordered {
    border: 0!important;
    margin-top:-5px!important;
}

.print-format td, .print-format th {
    padding:5px 5px !important;
    vertical-align:middle!important;
}

.print-format .primary.compact-item {
    font-weight: normal;
}

.print-format table td .value {
    font-size: 10px;
    line-height: 1.8;
}


.report-summary {
    background-color: var(--bg-color);
    border-radius: var(--border-radius-md, 8px);
    border-bottom: 1px solid var(--border-color);
    margin: 1rem;
    padding: 5px 5px;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: space-evenly;

}


.report-summary .summary-value {
    font-size: 14px;
    font-weight: 600;
    line-height: 16px;
    color: var(--text-color);
    font-feature-settings: "tnum";
    padding-top: 12px;
    padding-bottom: 5px;
    text-align: center;
    overflow: hidden !important;
    text-overflow: ellipsis;
    white-space: nowrap;
    

  
}

.report-summary .summary-label {
    font-size: var(--text-base);
    font-weight: normal !important;
    text-align: center;
    overflow: hidden !important;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.report-summary .summary-item {
    margin:0px 15px;
    min-width: 160;
    color: var(--text-color);
    max-width: 300px;
    height: 50px;
    display: flex;
    flex-direction: column;
    place-content: center;
    align-content: center;
    justify-content: center;
}

.summary-item .summary-value.red {
    color: var(--red-500);
}

.summary-item .summary-value.blue {
    color: var(--blue-500);
}

.summary-item .summary-value.green {
    color: var(--green-500);
}

.summary-item .summary-value.orange {
    color: var(--orange-500);
}

.summary-item .summary-value.pink {
    color: pink
}


@media (orientation: portrait) {
     .print-format {
        font-size: 12px!important;
        padding:0in!important;
       
    }
    .report-summary {
        background-color: #f1f1f170;
        border-radius: var(--border-radius-md, 8px);
        border-bottom: 1px solid var(--border-color);
        margin: 1rem;
        padding: 5px 5px;
        display: flex;
        flex-wrap: wrap;
        align-items: center;
        justify-content: space-evenly;

    }
  
}


@media (orientation: landscape) {
   .print-format {
    font-size: 12px!important;
    padding:0in!important;
   
    }
    
    .report-summary {
        background-color: #f1f1f170;
        border-radius: var(--border-radius-md, 8px);
        border-bottom: 1px solid var(--border-color);
        margin: 1rem;
        padding: 5px 5px;
        display: flex;
        flex-wrap: wrap;
        align-items: center;
        justify-content: space-evenly;
    }
    
  }
