custom_overrides:
.for-login .page-card-head img, .for-forgot .page-card-head img, .for-login-with-email-link .page-card-head img, .for-signup .page-card-head img, .for-email-login .page-card-head img{
max-height:66px !important;
}

.navbar-light,.web-footer{
    background:#0181c0!important;

}
.navbar-light .navbar-brand span,.web-footer .footer-powered{
    color: #fff !important;
}

.btn-login{
    height:45px!important;
}
.forgot-password-message{
    display: none;
}
.page-card-head h4{
    margin: auto 27px;
}
.web-footer{
    min-height: 0 !important;
    padding: 0 !important;
}
.form-signin.form-login .social-logins{
    display: none !important;
}

